CREATE OR REPLACE PROCEDURE gerar_interf_apac ( cd_estabelecimento_p bigint, nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w			bigint		:= 0;
nr_interno_conta_w			bigint		:= 0;
nr_atendimento_w			bigint		:= 0;
qt_apac_gravada_w		bigint		:= 0;

C02 CURSOR FOR 
SELECT 	 a.nr_interno_conta, 
	 a.nr_atendimento 
 from 	 conta_paciente a, 
	 atendimento_paciente b 
 where 	 a.nr_atendimento	= b.nr_atendimento 
 and 	 a.nr_seq_protocolo	= nr_seq_protocolo_p 
order by 1;

 

BEGIN 
 
/* Limpar tabelas transit√≥rias */
 
delete from w_interf_apac_cab;
delete from w_interf_apac_det;
commit;
 
 
/* Tratar registro tipo CABECALHO */
 
/* Buscar sequence */
 
select nextval('w_interf_apac_cab_seq') 
into STRICT	 nr_sequencia_w
;
 
/* Gravar W_INTERF_APAC_CAB */
 
insert 	 into W_INTERF_APAC_CAB( 
	 NR_SEQUENCIA, 
	 DT_ATUALIZACAO, 
	 NM_USUARIO, 
	 IE_IDENTIFICACAO, 
	 DT_COMPETENCIA, 
	 QT_APAC_GRAVADA, 
	 NR_CONTROLE, 
	 DS_RAZAO_SOCIAL, 
	 IE_SIGLA_APAC, 
	 CD_CGC, 
	 NM_ORGAO_DESTINO_APAC, 
	 IE_ORGAO_DESTINO_APAC, 
	 DT_GERACAO, 
	 IE_FIM_REGISTRO) 
SELECT 	 nr_sequencia_w, 
	 clock_timestamp(), 
	 nm_usuario_p, 
	 '*APAC', 
	 a.dt_competencia_apac, 
	 0, 
	 0, 
	 substr(c.ds_razao_social,1,30), 
	 a.ie_sigla_apac, 
	 b.cd_cgc, 
	 a.nm_orgao_destino_apac, 
	 a.ie_orgao_destino_apac, 
	 clock_timestamp(), 
	 null 
from	pessoa_juridica c, 
	estabelecimento b, 
	sus_parametros a 
where 	a.cd_estabelecimento 	= cd_estabelecimento_p 
and 	a.cd_estabelecimento 	= b.cd_estabelecimento 
and	b.cd_cgc		= c.cd_cgc;
 
commit;
 
/* selecionar as contas do protocolo */
 
qt_apac_gravada_w := 0;			
 
OPEN C02;
LOOP 
FETCH C02 	into 
		nr_interno_conta_w, 
		nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
   		BEGIN 
		CALL Gerar_Interf_APAC_Det(cd_estabelecimento_p, 
					nr_seq_protocolo_p, 
					nr_atendimento_w, 
					nr_interno_conta_w, 
					nm_usuario_p);
		END;
END LOOP;
close C02;
 
select count(*) 
into STRICT	 qt_apac_gravada_w 
from	 w_interf_apac_det;
 
update W_INTERF_APAC_CAB 
set qt_apac_gravada = qt_apac_gravada_w 
where nr_sequencia = nr_sequencia_w;
commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
