		k.dt_vencimento_atual				dt_limite, 
		d.vl_titulo, 
		c.vl_desconto, 
		0						vl_abatimento, 
		0						vl_mora, 
		NULL						dt_desconto, 
		c.vl_acrescimo, 
		clock_timestamp()				dt_pagto, --(dt_limite) 
		c.vl_escritural 				vl_pagto, 
		0 						vl_total_pagto, 
		0 						qt_lotes_arquivo, 
		0 						qt_total_registros, 
		''						cd_moeda, 
		k.nr_nosso_numero				ds_nosso_numero, 
		substr(((0409 + (c.cd_agencia_bancaria)::numeric  + (c.nr_conta)::numeric  + c.vl_escritural)*5),1,20) cd_camara 
	FROM	Estabelecimento e, 
		banco_estabelecimento_v b, 
		banco_escritural a, 
		titulo_pagar_v2 d,	 
		titulo_pagar_v k, 
		titulo_pagar_escrit c 
	WHERE	c.nr_titulo		= d.nr_titulo 
	AND	k.nr_titulo		= d.nr_titulo 
	AND	a.nr_sequencia		= c.nr_seq_escrit 
	AND	a.cd_estabelecimento	= e.cd_estabelecimento 
	AND 	b.ie_tipo_relacao	IN ('EP','ECC') 
	AND	a.nr_seq_conta_banco	= b.nr_sequencia 
	AND 	c.ie_tipo_pagamento	= 'BLQ' 
	AND	c.cd_banco		<> 409 
	
UNION
 
	/* Trailler do Arquivo*/
 
	SELECT	7						nr_seq_arquivo, 
		7						tp_registro, 
		0 						nr_seq_forma_pagto, 
		'0'						nr_inscricao, 
		'0' 						cd_agencia, 
		'0' 						cd_conta, 
		'' 						ds_dac_agencia, 
		''						ie_digito_conta, 
		'' 						nm_empresa, 
		'' 						ds_banco, 
		clock_timestamp() 					dt_arquivo, 
		e.nr_sequencia 					nr_seq_envio, 
		' ' 						ie_tipo_fornecedor, 
		'0' 						cd_cgc_fornecedor, 
		'' 						nm_hospital, 
		'' 						ds_endereco, 
		'0' 						nr_endereco, 
		' '						ds_complemento, 
		' '						ds_bairro, 
		'' 						ds_cidade, 
		'0' 						cd_cep, 
		'' 						ds_estado, 
		0 						cd_banco_fornecedor, 
		'0' 						cd_agencia_fornecedor, 
		'0'						nr_conta_fornecedor, 
		'' 						cd_agencia_conta, 
		''						nm_fornecedor, 
		99999						nr_documento, 
		clock_timestamp() 					dt_vencimento, 
		0 						vl_saldo_titulo, 
		0 						qt_registros, 
		0						ie_tipo_titulo, 
		'' 						ie_cod_barras, 
		clock_timestamp()					dt_emissao, 
		clock_timestamp()					dt_limite, 
		0 						vl_titulo, 
		0 						vl_desconto, 
		0						vl_abatimento, 
		0						vl_mora, 
		NULL						dt_desconto, 
		0 						vl_acrescimo, 
		clock_timestamp() 					dt_pagto, 
		0 						vl_pagto, 
		SUM(c.vl_escritural)				vl_total_pagto, 
		COUNT(*) 					qt_lotes_arquivos, 
		COUNT(*) 					qt_total_registros, 
		''						cd_moeda, 
		''						ds_nosso_numero, 
		''						cd_camara 
	FROM	banco_estabelecimento_v b, 
		estabelecimento a,    
		banco_escritural e, 
		titulo_pagar_escrit c 
	WHERE	e.cd_estabelecimento  = a.cd_estabelecimento 
	AND	b.ie_tipo_relacao    IN ('EP','ECC') 
	AND	e.nr_sequencia		= c.nr_seq_escrit 
	AND	b.nr_sequencia		= e.nr_seq_conta_banco 
	GROUP BY e.nr_sequencia 
	ORDER BY nr_seq_arquivo, 
		nr_documento, 
		tp_registro) alias83 
where	nr_seq_envio = nr_seq_envio_p;


BEGIN 
 
delete	from w_interf_itau;
commit;
 
open c01;
loop 
fetch c01 into 
	nr_seq_arquivo_w, 
	tp_registro_w, 
	nr_seq_forma_pagto_w, 
	nr_inscricao_w, 
	cd_agencia_w, 
	cd_conta_w, 
	ds_dac_agencia_w, 
	ie_digito_conta_w, 
	nm_empresa_w, 
	ds_banco_w, 
	dt_arquivo_w, 
	nr_seq_envio_w, 
	ie_tipo_fornecedor_w, 
	cd_cgc_fornecedor_w, 
	nm_hospital_w, 
	ds_endereco_w, 
	nr_endereco_w, 
	ds_complemento_w, 
	ds_bairro_w, 
	ds_cidade_w, 
	cd_cep_w, 
	ds_estado_w, 
	cd_banco_fornecedor_w, 
	cd_agencia_fornecedor_w, 
	nr_conta_fornecedor_w, 
	cd_agencia_conta_w, 
	nm_fornecedor_w, 
	nr_documento_w, 
	dt_vencimento_w, 
	vl_saldo_titulo_w, 
	qt_registros_w, 
	ie_tipo_titulo_w, 
	ie_cod_barras_w, 
	dt_emissao_w, 
	dt_limite_w, 
	vl_titulo_w, 
	vl_desconto_w, 
	vl_abatimento_w, 
	vl_mora_w, 
	dt_desconto_w, 
	vl_acrescimo_w, 
	dt_pagto_w, 
	vl_pagto_w, 
	vl_total_pagto_w, 
	qt_lotes_arquivo_w, 
	qt_total_registros_w, 
	cd_moeda_w, 
	ds_nosso_numero_w, 
	cd_camara_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
insert	into	w_interf_itau(nr_sequencia, 
		nm_usuario, 
		dt_atualizacao, 
		nm_usuario_nrec, 
		dt_atualizacao_nrec, 
		nr_seq_apres, 
		ie_tipo_registro, 
		nr_lote, 
		nr_inscricao, 
		cd_agencia_bancaria, 
		cd_conta, 
		ds_dac_agencia, 
		ie_digito_conta, 
		nm_empresa,	 
		ds_banco, 
		dt_geracao, 
		nr_seq_envio, 
		ie_tipo_inscricao, 
		cd_cgc_fornecedor, 
		cd_carteira, 
		ds_endereco, 
		nr_endereco, 
		ds_complemento, 
		ds_bairro, 
		ds_cidade, 
		cd_cep, 
		sg_estado, 
		cd_banco_fornec, 
		nm_fornecedor, 
		nr_documento, 
		dt_vencimento, 
		vl_saldo_titulo, 
		qt_registros, 
		ie_forma_lancamento, 
		cd_barras, 
		vl_titulo, 
		vl_desconto, 
		vl_acrescimo, 
		dt_pagto, 
		vl_pagto, 
		vl_total_pagto, 
		qt_lote_arquivo, 
		qt_tot_reg, 
		nr_registro, 
		cd_camara_compensacao, 
		nr_seq_reg_lote, 
		nr_conta_fornecedor, 
		cd_agencia_fornec, 
		nr_nosso_numero) 
	values (nextval('w_interf_itau_seq'), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nr_seq_apres_w, 
		tp_registro_w, 
		nr_lote_w, 
		nr_inscricao_w, 
		cd_agencia_w, 
		cd_conta_w, 
		ds_dac_agencia_w, 
		ie_digito_conta_w, 
		nm_empresa_w, 
		ds_banco_w, 
		dt_arquivo_w, 
		nr_seq_envio_w, 
		ie_tipo_fornecedor_w, 
		cd_cgc_fornecedor_w, 
		cd_agencia_conta_w, 
		ds_endereco_w, 
		nr_endereco_w, 
		ds_complemento_w, 
		ds_bairro_w, 
		ds_cidade_w, 
		cd_cep_w, 
		ds_estado_w, 
		cd_banco_fornecedor_w, 
		nm_fornecedor_w, 
		nr_documento_w, 
		dt_vencimento_w, 
		vl_saldo_titulo_w, 
		qt_registros_w, 
		ie_tipo_titulo_w, 
		ie_cod_barras_w, 
		vl_titulo_w, 
		vl_desconto_w, 
		vl_acrescimo_w, 
		dt_pagto_w, 
		vl_pagto_w, 
		vl_total_pagto_w, 
		qt_lotes_arquivo_w, 
		qt_total_registros_w, 
		nr_registro_w, 
		cd_camara_w, 
		nr_seq_reg_lote_w, 
		nr_conta_fornecedor_w, 
		cd_agencia_fornecedor_w, 
		ds_nosso_numero_w);
 
nr_seq_apres_w := nr_seq_apres_w + 1;
 
/* So incrementar o numero do registro quando detalhe */
 
if (tp_registro_w in ('2','3','4','5','6')) then 
	nr_registro_w		:= nr_registro_w + 1;
	end if;
 
end loop;
close c01;
 
commit;
 
end;
$body$
