CREATE OR REPLACE PROCEDURE gerar_item_cme_conjunto ( nr_sequencia_p bigint, nr_seq_agenda_p bigint, nr_seq_proc_interno_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_anos_pac_w			smallint;
qt_meses_pac_w			smallint;
qt_total_meses_pac_w	smallint;

c01 CURSOR FOR
	SELECT	distinct
		d.nr_sequencia,
		d.qt_conjunto
	from   	cm_grupo_conjunto a,
		cm_grupo_classif b,
		cm_classif_conjunto c,
		cm_conjunto d
	where  	a.nr_sequencia = b.nr_seq_grupo
	and	b.nr_seq_classificacao = c.nr_sequencia
	and	c.nr_sequencia = d.nr_seq_classif
	and	coalesce(a.ie_situacao,'A') = 'A'
	and	coalesce(c.ie_situacao,'A') = 'A'
	and	coalesce(d.ie_situacao,'A') = 'A'
	and	a.nr_sequencia = nr_sequencia_p
	and coalesce(qt_anos_pac_w,0) between coalesce(d.qt_idade_min,0) and  coalesce(d.qt_idade_max,999)
	and	not exists (SELECT 1 from agenda_pac_cme x where x.nr_seq_agenda = nr_seq_agenda_p and x.nr_seq_conjunto = d.nr_sequencia);

nr_seq_conjunto_w	bigint;
qt_conjunto_w 		bigint;


BEGIN
select  max(qt_idade_paciente),
		max(qt_idade_mes)
into STRICT	qt_anos_pac_w,
        qt_meses_pac_w
from	agenda_paciente
where	nr_sequencia 	= 	nr_seq_agenda_p;

open	c01;
loop
fetch	c01 into
	nr_seq_conjunto_w,
	qt_conjunto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	insert 	into agenda_pac_cme(
		nr_sequencia,
		nr_seq_agenda,
		dt_atualizacao,
		nm_usuario,
		nr_seq_conjunto,
		qt_conjunto,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_origem_inf,
		nr_seq_grupo,
		ie_obrigatorio,
		nr_seq_proc_interno)
	values (nextval('agenda_pac_cme_seq'),
		nr_seq_agenda_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_conjunto_w,
		coalesce(qt_conjunto_w,1),
		clock_timestamp(),
		nm_usuario_p,
		'I',
		nr_sequencia_p,
		'S',
		CASE WHEN coalesce(nr_seq_proc_interno_p,0)=0 THEN null  ELSE nr_seq_proc_interno_p END );
	end;
end loop;
close c01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
