SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_item_requisicao_material (nr_requisicao_p bigint, cd_material_p bigint, qt_material_p bigint, nr_seq_lote_fornec_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


cd_estab_w			integer;
cd_oper_estoque_w		smallint;
cd_setor_atend_w			integer;
cd_local_estoque_w		smallint;
cd_centro_custo_w			integer;
ie_tipo_conta_w			smallint;
cd_conta_contabil_w		varchar(20) := '';
nr_sequencia_w			bigint;
cd_cgc_fornec_w			varchar(14);
cd_unid_med_consumo_w		varchar(30);
cd_unid_med_estoque_w		varchar(30);
qt_conv_estoque_consumo_w 	double precision;
ie_baixa_estoq_pac_w		varchar(1);
dt_solicitacao_requisicao_w		timestamp;
ds_consistencia_w			varchar(255);
ds_erro_w			varchar(255) := '';
VarConsisteMaterialCCusto_w		varchar(01);
VarConsisteMaterialLocal_w		varchar(01);
VarPermiteQtMaiorPadrao_w		varchar(01);

c01 CURSOR FOR
SELECT	ds_consistencia
from	requisicao_mat_consist
where	nr_requisicao = nr_requisicao_p;


BEGIN


select	cd_estabelecimento,
	cd_operacao_estoque,
	cd_setor_atendimento,
	cd_local_estoque,
	cd_centro_custo,
	dt_solicitacao_requisicao
into STRICT	cd_estab_w,
	cd_oper_estoque_w,
	cd_setor_atend_w,
	cd_local_estoque_w,
	cd_centro_custo_w,
	dt_solicitacao_requisicao_w
from	requisicao_material
where	nr_requisicao = nr_requisicao_p;

VarConsisteMaterialCCusto_w	:= substr(coalesce(obter_valor_param_usuario(919, 4, Obter_perfil_ativo, nm_usuario_p, cd_estab_w),'N'),1,1);
VarConsisteMaterialLocal_w	:= substr(coalesce(obter_valor_param_usuario(919, 5, Obter_perfil_ativo, nm_usuario_p, cd_estab_w),'N'),1,1);
VarPermiteQtMaiorPadrao_w	:= substr(coalesce(obter_valor_param_usuario(919, 69, Obter_perfil_ativo, nm_usuario_p, cd_estab_w),'N'),1,1);


SELECT * FROM Consistir_Item_Requisicao(cd_material_p, nr_requisicao_p, VarConsisteMaterialCCusto_w, 'N', VarConsisteMaterialLocal_w, 'N', VarPermiteQtMaiorPadrao_w, 'S', nm_usuario_p, 'N', cd_cgc_fornec_w, ds_erro_p) INTO STRICT cd_cgc_fornec_w, ds_erro_p;

open C01;
loop
fetch C01 into	
	ds_consistencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_erro_w := substr(ds_erro_w || ds_consistencia_w || chr(13) || chr(10),1,255);
	end;
end loop;
close C01;

if (coalesce(ds_erro_w::text, '') = '') then

	select	CASE WHEN ie_tipo_requisicao=2 THEN 2 WHEN ie_tipo_requisicao=21 THEN 2  ELSE 3 END
	into STRICT	ie_tipo_conta_w
	from	operacao_estoque
	where	cd_operacao_estoque = cd_oper_estoque_w;

	SELECT * FROM define_conta_material(
			cd_estab_w, cd_material_p, ie_tipo_conta_w, 0, cd_setor_atend_w, '0', 0, 0, 0, 0, cd_local_estoque_w, Null, dt_solicitacao_requisicao_w, cd_conta_contabil_w, cd_centro_custo_w, null) INTO STRICT cd_conta_contabil_w, cd_centro_custo_w;

	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_sequencia_w
	from	item_requisicao_material
	where	nr_requisicao = nr_requisicao_p;

	select	substr(obter_dados_material_estab(cd_material,cd_estab_w,'UMS'),1,30) cd_unidade_medida_consumo,
		substr(obter_dados_material_estab(cd_material,cd_estab_w,'UME'),1,30) cd_unidade_medida_estoque,
		qt_conv_estoque_consumo,
		/*ie_baixa_estoq_pac Fabio - 23/06/2004 Alterei para buscar da function*/

		substr(obter_material_baixa_estoq_pac(cd_estab_w, 0, cd_material),1,1)
	into STRICT	cd_unid_med_consumo_w,
		cd_unid_med_estoque_w,
		qt_conv_estoque_consumo_w,
		ie_baixa_estoq_pac_w
	from	material
	where	cd_material = cd_material_p;
end if;
	
ds_erro_p := consiste_minimo_multiplo_req(cd_material_p, qt_material_p, ds_erro_p);
	
if (coalesce(ds_erro_w::text, '') = '') then
	

	insert into item_requisicao_material(
		NR_REQUISICAO,
		NR_SEQUENCIA,
		CD_ESTABELECIMENTO,
		CD_MATERIAL,
		QT_MATERIAL_REQUISITADA,
		QT_MATERIAL_ATENDIDA,
		VL_MATERIAL,
		DT_ATUALIZACAO,
		NM_USUARIO,
		CD_UNIDADE_MEDIDA,
		DT_ATENDIMENTO,
		CD_PESSOA_RECEBE,
		CD_PESSOA_ATENDE,
		IE_ACAO,
		CD_MOTIVO_BAIXA,
		QT_ESTOQUE,
		CD_UNIDADE_MEDIDA_ESTOQUE,
		CD_CONTA_CONTABIL,
		CD_MATERIAL_REQ,
		NR_SEQ_LOTE_FORNEC,
		CD_CGC_FORNECEDOR,
		DS_OBSERVACAO,
		IE_GERACAO)
	values (
		nr_requisicao_p,
		nr_sequencia_w,
		cd_estab_w,
		cd_material_p,
		qt_material_p,
		0,
		0,
		clock_timestamp(),
		nm_usuario_p,
		cd_unid_med_consumo_w,
		null,
		null,
		null,
		'1',
		0,
		(qt_material_p / qt_conv_estoque_consumo_w),
		cd_unid_med_estoque_w,
		cd_conta_contabil_w,
		cd_material_p,
		CASE WHEN nr_seq_lote_fornec_p=0 THEN null END ,
		cd_cgc_fornec_w,
		null,
		null);
		
	-- WHEB_MENSAGEM_PCK.get_texto(355122) - Inclusão de item

	-- WHEB_MENSAGEM_PCK.get_texto(355133) - Item [#@CD_MATERIAL#@] - #@DS_MATERIAL#@ incluido através do Atendimento da Requisição pelo usuário #@NM_USUARIO#@.

	-- AR - Inclusão de item via atendimento requisição
	CALL gerar_historico_requisicao(
			nr_requisicao_p,
			WHEB_MENSAGEM_PCK.get_texto(355122),
			WHEB_MENSAGEM_PCK.get_texto(355133, 'CD_MATERIAL=' || cd_material_p || ';DS_MATERIAL=' || obter_desc_material(cd_material_p) || ';NM_USUARIO=' || Obter_Desc_Usuario(nm_usuario_p)),
			'AR',
			nm_usuario_p);
	
end if;

ds_erro_p := ds_erro_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_item_requisicao_material (nr_requisicao_p bigint, cd_material_p bigint, qt_material_p bigint, nr_seq_lote_fornec_p bigint, nm_usuario_p text, ds_erro_p INOUT text) FROM PUBLIC;

