CREATE OR REPLACE PROCEDURE gerar_item_result_lib_sae ( nr_seq_item_p bigint, ds_lista_resul_p text, nm_usuario_p text) AS $body$
DECLARE


lista_resultado_w	varchar(800);
ie_contador_w		bigint := 0;
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
nr_seq_result_w		bigint;
nr_sequencia_w		bigint;


BEGIN

lista_resultado_w	:= ds_lista_resul_p;

while 	((lista_resultado_w IS NOT NULL AND lista_resultado_w::text <> '') or (ie_contador_w > 200)) loop
	begin
	tam_lista_w		:= length(lista_resultado_w);
	ie_pos_virgula_w		:= position(',' in lista_resultado_w);
	if (ie_pos_virgula_w <> 0) then
		begin
		nr_seq_result_w 	:= substr(lista_resultado_w,1,(ie_pos_virgula_w - 1));
		lista_resultado_w 	:= substr(lista_resultado_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end;
	end if;

	select	nextval('pe_sae_mod_item_result_seq')
	into STRICT	nr_sequencia_w
	;

	insert into pe_sae_mod_item_result(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_item,
		nr_seq_resultado )
	values ( nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_item_p,
		nr_seq_result_w );
	ie_contador_w			:= ie_contador_w + 1;
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
