SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_itens_auditoria_proc_mat ( nr_sequencia_p bigint, nr_seq_auditoria_p bigint, ie_tipo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w		bigint;
nr_seq_item_w		bigint;
qt_item_w		double precision;
vl_item_w			double precision;
ie_status_acerto_w		varchar(01);
nr_interno_conta_w		bigint;
ie_exige_auditoria_w	varchar(1) := 'N';
tx_procedimento_w		double precision;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.qt_material, 
		a.vl_material 
	from	estrutura_material_v b, 
		material_atend_paciente a 
	where	a.cd_material = b.cd_material 
	and	a.nr_sequencia = nr_sequencia_p 
	and	a.nr_interno_conta = nr_interno_conta_w 
	
union all
 
	SELECT	a.nr_sequencia, 
		a.qt_material, 
		a.vl_material 
	from	material_atend_paciente a 
	where	a.nr_seq_orig_audit = nr_sequencia_p 
	and	a.nr_interno_conta = nr_interno_conta_w;
	
C02 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.qt_procedimento, 
		a.tx_procedimento, 
		a.vl_procedimento 
	from	estrutura_procedimento_v b, 
		procedimento_paciente a 
	where	a.cd_procedimento = b.cd_procedimento 
	and	a.ie_origem_proced = b.ie_origem_proced 
	and	a.nr_sequencia = nr_sequencia_p 
	and	a.nr_interno_conta = nr_interno_conta_w 
	
union all
 
	SELECT	a.nr_sequencia, 
		a.qt_procedimento, 
		a.tx_procedimento, 
		a.vl_procedimento 
	from	procedimento_paciente a 
	where	a.nr_seq_orig_audit = nr_sequencia_p 
	and	a.nr_interno_conta = nr_interno_conta_w;


BEGIN 
 
select	max(nr_interno_conta) 
into STRICT	nr_interno_conta_w 
from	auditoria_conta_paciente 
where	nr_sequencia = nr_seq_auditoria_p;
 
select	max(ie_status_acerto) 
into STRICT	ie_status_acerto_w 
from	conta_paciente 
where	nr_interno_conta = nr_interno_conta_w;
 
if (ie_status_acerto_w = 2) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(192255);
end if;	
 
if (ie_tipo_p = 1) then 
 
	open C01;
	loop 
	fetch C01 into	 
		nr_sequencia_w, 
		qt_item_w, 
		vl_item_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
 
		select	max(obter_se_exige_auditoria(nr_sequencia_w,1)) 
		into STRICT	ie_exige_auditoria_w 
		;
		 
		select	nextval('auditoria_matpaci_seq') 
		into STRICT	nr_seq_item_w 
		;
		 
		insert	into auditoria_matpaci(nr_sequencia, 
			nr_seq_auditoria, 
			nr_seq_matpaci, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_seq_motivo, 
			qt_original, 
			qt_ajuste, 
			ds_justificativa, 
			ie_tipo_auditoria, 
			ie_perda, 
			vl_material, 
			ie_exige_auditoria) 
		values (	nr_seq_item_w, 
			nr_seq_auditoria_p, 
			nr_sequencia_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			null, 
			qt_item_w, 
			null, 
			null, 
			'A', 
			'N', 
			vl_item_w, 
			ie_exige_auditoria_w);
 
		update	material_atend_paciente 
		set	ie_auditoria	= 'A' 
		where	nr_sequencia	= nr_sequencia_w;
 
	 
		end;
	end loop;
	close C01;
 
elsif (ie_tipo_p = 2) then 
 
	open C02;
	loop 
	fetch C02 into	 
		nr_sequencia_w, 
		qt_item_w, 
		tx_procedimento_w, 
		vl_item_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		 
		select	max(obter_se_exige_auditoria(nr_sequencia_w,2)) 
		into STRICT	ie_exige_auditoria_w 
		;
 
		select	nextval('auditoria_propaci_seq') 
		into STRICT	nr_seq_item_w 
		;
 
		insert	into auditoria_propaci(nr_sequencia, 
			nr_seq_auditoria, 
			nr_seq_propaci, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_seq_motivo, 
			qt_original, 
			qt_ajuste, 
			ds_justificativa, 
			tx_proc_original, 
			ie_tipo_auditoria, 
			ie_perda, 
			vl_procedimento, 
			ie_exige_auditoria) 
		values (nr_seq_item_w, 
			nr_seq_auditoria_p, 
			nr_sequencia_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			null, 
			qt_item_w, 
			null, 
			null, 
			coalesce(tx_procedimento_w,0), 
			'A', 'N', 
			vl_item_w, 
			ie_exige_auditoria_w);
 
		update	procedimento_paciente 
		set	ie_auditoria	= 'A' 
		where	nr_sequencia	= nr_sequencia_w;
		 
		end;
	end loop;
	close C02;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_itens_auditoria_proc_mat ( nr_sequencia_p bigint, nr_seq_auditoria_p bigint, ie_tipo_p bigint, nm_usuario_p text) FROM PUBLIC;

