CREATE OR REPLACE PROCEDURE gerar_itens_avaliar_curativo ( nr_sequencia_p bigint, cd_empresa_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_sequencia_w		bigint;
nr_seq_w			bigint;
cd_estabelecimento_w	smallint;

C01 CURSOR FOR
SELECT	nr_sequencia
from	cur_item
where	cd_empresa = cd_empresa_p
and	ie_situacao = 'A'
and		coalesce(cd_estabelecimento,coalesce(cd_estabelecimento_w,0)) = coalesce(cd_estabelecimento_w,0)
order by nr_seq_apres, ds_item;


BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

OPEN C01;
LOOP
	FETCH C01 INTO
	nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	select	nextval('cur_curativo_item_seq')
	into STRICT	nr_seq_w
	;

	insert into cur_curativo_item(
		nr_sequencia,
		nr_seq_curativo,
		dt_atualizacao,
		nm_usuario,
		nr_seq_item,
		nr_seq_result,
		qt_valor)
	values (
		nr_seq_w,
		nr_sequencia_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_w,
		null,
		0);
END LOOP;
CLOSE C01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
