CREATE OR REPLACE PROCEDURE gerar_itens_avaliar_gca ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w	bigint;
nr_seq_w	bigint;
qt_idade_w	bigint;
cd_setor_atendimento_w	bigint;
ie_autor_w	varchar(1);

c01 CURSOR FOR 
SELECT	nr_sequencia 
from	gca_item 
where	ie_situacao = 'A' 
and		coalesce(cd_setor_atendimento,cd_setor_atendimento_w)	= cd_setor_atendimento_w 
and		qt_idade_w	between coalesce(OBTER_IDADE_GCA_item(nr_sequencia,'MIN'),0) 	and	coalesce(OBTER_IDADE_GCA_item(nr_sequencia,'MAX'),9999999) 
and		((coalesce(ie_autor_w::text, '') = '') or (coalesce(ie_autor,ie_autor_w) = ie_autor_w)) 
order by nr_seq_apres;	
 

BEGIN 
 
select	coalesce(max(obter_idade_pf(obter_pessoa_atendimento(nr_atendimento,'C'),clock_timestamp(),'DIA')),0), 
		coalesce(max(obter_setor_atendimento(nr_atendimento)),0), 
		max(ie_autor) 
into STRICT	qt_idade_w, 
		cd_setor_atendimento_w, 
		ie_autor_w 
from	gca_atendimento 
where	nr_sequencia = nr_sequencia_p;
 
open c01;
loop 
	fetch c01 into 
	nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
 
	select	nextval('gca_atend_result_seq') 
	into STRICT	nr_seq_w 
	;
	 
	insert into gca_atend_result( 
		nr_sequencia, 
		nr_seq_avaliacao, 
		nr_seq_item, 
		dt_atualizacao, 
		nm_usuario, 
		nr_seq_result, 
		qt_pontuacao) 
	values ( 
		nr_seq_w, 
		nr_sequencia_p, 
		nr_sequencia_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		null, 
		0);
 
end loop;
close c01;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
