CREATE OR REPLACE PROCEDURE gerar_itens_avaliar_srpa ( nr_sequencia_p bigint, nr_seq_modelo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
nr_seq_w	bigint;

C01 CURSOR FOR
SELECT	nr_sequencia
from	srpa_item
where	nr_seq_modelo = nr_seq_modelo_p
order by nr_seq_apres;


BEGIN

OPEN C01;
LOOP
	FETCH C01 INTO
	nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	select	nextval('srpa_aval_item_seq')
	into STRICT	nr_seq_w
	;

	insert into srpa_aval_item(
		nr_sequencia,
		nr_seq_avaliacao,
		nr_seq_item,
		dt_atualizacao,
		nm_usuario,
		nr_seq_result,
		qt_result)
	values (
		nr_seq_w,
		nr_sequencia_p,
		nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		null,
		0);

END LOOP;
CLOSE C01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
