CREATE OR REPLACE PROCEDURE gerar_itens_caixa_opme_autor (nr_sequencia_autor_p bigint, nr_seq_caixa_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w	integer;
qt_material_w	double precision;
ds_caixa_w	varchar(255);
cont_w		bigint;

c01 CURSOR FOR
SELECT 	a.ds_tipo_caixa,
	b.cd_material,
	b.qt_item
from 	opme_tipo_caixa a,
	opme_tipo_caixa_item b
where	a.nr_sequencia		= nr_seq_caixa_p
and	a.nr_sequencia		= b.nr_seq_tipo;


BEGIN

if (nr_seq_caixa_p IS NOT NULL AND nr_seq_caixa_p::text <> '') then
	open C01;
	loop
	fetch C01 into	
		ds_caixa_w,
		cd_material_w,
		qt_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		
		select	count(*)
		into STRICT	cont_w
		from	material_autorizado
		where	nr_sequencia_autor	= nr_sequencia_autor_p
		and	cd_material		= cd_material_w;

		if (cont_w = 0) then
	
			insert	into	material_autorizado(nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				cd_material,
				qt_solicitada,
				nr_sequencia_autor,
				qt_autorizada,
				ds_observacao)
			values (nextval('material_autorizado_seq'),
				clock_timestamp(),
				nm_usuario_p,
				cd_material_w,
				qt_material_w,
				nr_sequencia_autor_p,
				0,
				obter_desc_expressao(726278) || ' ' || ds_caixa_w);
		else
			update	material_autorizado
			set	qt_solicitada		= qt_solicitada + qt_material_w,
				dt_atualizacao 		= clock_timestamp(),
				nm_usuario		= nm_usuario_p
			where	nr_sequencia_autor	= nr_sequencia_autor_p
			and	cd_material		= cd_material_w;
		end if;
	
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
