CREATE OR REPLACE PROCEDURE gerar_itens_controle ( nr_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w		bigint := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

insert into cirurgia_item_controle(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_cirurgia,
	nr_seq_item_controle,
	ie_acao,
	qt_item,
	qt_peso,
	ie_situacao)
SELECT	nextval('cirurgia_item_controle_seq'),
	clock_timestamp(),
	'Tasy',
	clock_timestamp(),
	nm_usuario_p,
	nr_cirurgia_p,
	nr_sequencia,
	1,
	0,
	0,
	'A'
from	pepo_item_controle
where 	((cd_estabelecimento = cd_estabelecimento_w) or (coalesce(cd_estabelecimento::text, '') = ''));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
