CREATE OR REPLACE PROCEDURE gerar_itens_nc_curva_abc ( nr_sequencia_p bigint, nr_seq_grupo_p bigint, nr_seq_segmento_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segmento_w		bigint;
nr_seq_grupo_w			bigint;
cd_material_w			bigint;
ie_curva_abc_seg_w		varchar(1);
ie_curva_abc_item_w		varchar(1);

c01 CURSOR FOR
/*Quando informa por Agrupamento*/

SELECT	b.nr_sequencia,
	a.nr_seq_grupo,
	c.cd_material,
	b.ie_curva_abc,
	c.ie_curva_abc
from	segmento_compra_vinc_grupo a,
	segmento_compras b,
	segmento_compras_estrut c
where	a.nr_seq_segmento	= b.nr_sequencia
and	c.nr_seq_segmento	= b.nr_sequencia
and	(c.cd_material IS NOT NULL AND c.cd_material::text <> '')
and	a.nr_seq_grupo		= nr_seq_grupo_p
and	ie_opcao_p		= 0

union

/*Quando Ã© por Segmento ou Todos*/

select	b.nr_sequencia,
	null,
	c.cd_material,
	b.ie_curva_abc,
	c.ie_curva_abc
from	segmento_compras b,
	segmento_compras_estrut c
where	c.nr_seq_segmento	= b.nr_sequencia
and	(c.cd_material IS NOT NULL AND c.cd_material::text <> '')
and	((nr_seq_segmento_p	= 0) or
	(nr_seq_segmento_p	> 0 AND b.nr_sequencia = nr_seq_segmento_p))
and	ie_opcao_p		in (1,2);


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_segmento_w,
	nr_seq_grupo_w,
	cd_material_w,
	ie_curva_abc_seg_w,
	ie_curva_abc_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ie_curva_abc_item_w := obter_curva_abc_item_segmento(	clock_timestamp(), nr_seq_segmento_w, cd_material_w, ie_curva_abc_item_w);

	insert into nc_curva_abc_item(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_nc,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_segmento,
		nr_seq_grupo,
		cd_material,
		ie_curva_segmento,
		ie_curva_item,
		ie_curva_atual)
	values (	nextval('nc_curva_abc_item_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_segmento_w,
		nr_seq_grupo_w,
		cd_material_w,
		ie_curva_abc_seg_w,
		ie_curva_abc_item_w,
		null);
	end;
end loop;
close C01;

commit;

end;
$body$
