CREATE OR REPLACE PROCEDURE gerar_itens_npt ( nr_prescricao_p bigint, nr_sequencia_p bigint, nm_usuario_p text, ie_calcular_p text, qt_gotejamento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ds_erro_w		varchar(2000);


BEGIN

ds_erro_w := Gerar_Nut_Elemento_Paciente(nr_sequencia_p, nm_usuario_p, ie_calcular_p, 'S', ds_erro_w);

if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
	ds_erro_p := ds_erro_w;
else

	if (coalesce(qt_gotejamento_p,0) = 0) then
		CALL Calcular_Gotejamento_Nut_Pac(nr_sequencia_p);
	end if;

	CALL Calcula_Nut_Paciente_Elemento(nr_sequencia_p, nm_usuario_p);

	CALL Recalc_Elem_NPT_Ad_Perc(nr_prescricao_p, nr_sequencia_p);

end if;

end;
$body$
