CREATE OR REPLACE PROCEDURE gerar_itens_regra_opme_html5 ( nr_seq_agenda_p bigint, nm_usuario_p text, cd_procedimento_p bigint) AS $body$
DECLARE


cd_material_w		integer;
qt_material_w		double precision;
nr_sequencia_w		bigint;
ie_commit_w		varchar(1) := 'N';

c01 CURSOR FOR

SELECT 	b.cd_material,
	b.qt_material
from   	opme_regra_procedimento a,
	opme_regra_proc_item b
where  	a.nr_sequencia = b.nr_seq_regra
and	a.cd_procedimento = cd_procedimento_p;



BEGIN

open C01;
loop
fetch C01 into
	cd_material_w,
	qt_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (cd_procedimento_p > 0) then
		ie_commit_w := 'S';
		select 	nextval('agenda_pac_opme_seq')
		into STRICT	nr_sequencia_w
		;

		insert into agenda_pac_opme(
			cd_material,
			dt_atualizacao,
			dt_atualizacao_nrec,
			ie_autorizado,
			ie_origem_inf,
			ie_padrao,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_agenda,
			nr_seq_apres,
			nr_sequencia,
			qt_material)
		values (	cd_material_w,
			clock_timestamp(),
			clock_timestamp(),
			'P',
			'I',
			'S',
			nm_usuario_p,
			nm_usuario_P,
			nr_seq_agenda_p,
			500,
			nr_sequencia_w,
			qt_material_w);
	end if;
	end;
end loop;
close C01;

if (ie_commit_w = 'S') then
	commit;
end if;

end;
$body$
