SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_itens_saldo_negativo ( nr_seq_auditoria_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	b.cd_material cd_item,
		null,
		'M' ie_tipo_item,
		sum(coalesce(c.qt_ajuste, c.qt_original)) qt_saldo
	from    auditoria_conta_paciente a,
		material_atend_paciente  b,
		auditoria_matpaci        c
	where   a.nr_sequencia = nr_seq_auditoria_p
	and     a.nr_sequencia = c.nr_seq_auditoria
	and    	b.nr_sequencia = c.nr_seq_matpaci
	group by	b.cd_material
	having	sum(coalesce(c.qt_ajuste, c.qt_original)) < 0
	
union all

	SELECT	b.cd_procedimento cd_item,
		b.ie_origem_proced,
		'P' ie_tipo_item,
		sum(coalesce(c.qt_ajuste, c.qt_original)) qt_saldo
	from    auditoria_conta_paciente a,
		procedimento_paciente  b,
		auditoria_propaci        c
	where   a.nr_sequencia = nr_seq_auditoria_p
	and     a.nr_sequencia = c.nr_seq_auditoria
	and    	b.nr_sequencia = c.nr_seq_propaci
	group by	b.cd_procedimento,
		b.ie_origem_proced
	having	sum(coalesce(c.qt_ajuste, c.qt_original)) < 0;

cd_item_w	bigint;
ie_origem_proced_w	bigint;
ie_tipo_item_w	varchar(2);
qt_saldo_w	double precision;


BEGIN

delete from auditoria_inconsistencia
where nr_seq_auditoria = nr_seq_auditoria_p;

open C01;
loop
fetch C01 into
	cd_item_w,
	ie_origem_proced_w,
	ie_tipo_item_w,
	qt_saldo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into auditoria_inconsistencia(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_item,
		ie_origem_proced,
		ie_tipo_item,
		qt_saldo,
		nr_seq_auditoria)
	values (nextval('auditoria_inconsistencia_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_item_w,
		ie_origem_proced_w,
		ie_tipo_item_w,
		qt_saldo_w,
		nr_seq_auditoria_p);

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
