CREATE OR REPLACE PROCEDURE gerar_itens_score_flex_2 ( nr_sequencia_p bigint, nr_seq_escala_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_item_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	eif_escala_ii_item
	where	nr_seq_escala = nr_seq_escala_p
	and	ie_situacao = 'A'
	order by nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	Insert into Escala_eif_II_item(nr_sequencia,
					nr_seq_escala,
					nr_seq_item,
					nm_usuario,
					nm_usuario_nrec,
					dt_atualizacao,
					dt_atualizacao_nrec
					)
				values (
					nextval('escala_eif_ii_item_seq'),
					nr_sequencia_p,
					nr_seq_item_w,
					nm_usuario_p,
					nm_usuario_p,
					clock_timestamp(),
					clock_timestamp()
					);

end loop;
close C01;

commit;

end;
$body$
