CREATE OR REPLACE PROCEDURE gerar_justi_calculo_nf (nr_sequencia_p bigint, ds_justificativa_p text, nm_usuario_p text) AS $body$
DECLARE


dt_atualizacao_estoque_w	timestamp;


BEGIN

select 	dt_atualizacao_estoque
into STRICT	dt_atualizacao_estoque_w
from 	nota_fiscal
where 	nr_sequencia = nr_sequencia_p;

if (coalesce(dt_atualizacao_estoque_w::text, '') = '') then

insert into 	nota_fiscal_hist(nr_sequencia,
		nr_seq_nota,
		dt_atualizacao,
		cd_evento,
		ds_historico,
		nm_usuario)
values (nextval('nota_fiscal_hist_seq'),
		nr_sequencia_p,
		clock_timestamp(),
		4,
		substr(ds_justificativa_p,1,255),
		nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
