SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_katz_sae ((nr_sequencia_p bigint, nm_usuario_p text) is nr_atendimento_w bigint) RETURNS varchar AS $body$
DECLARE


	ie_retorno_w	varchar(15);
	nr_seq_result_escala_w	varchar(15);
	
BEGIN

	select	max(f.nr_seq_result_escala)
	into STRICT	nr_seq_result_escala_w
	from	pe_prescricao a,
		pe_prescr_item_result b,
		pe_regra_item_escala c,
		pe_regra_result_escala f
	where	a.nr_sequencia = nr_sequencia_p
	and	a.nr_sequencia = b.nr_seq_prescr
	and	b.nr_seq_item = c.nr_seq_item_sae
	and	c.nr_seq_item_escala = nr_seq_item_escala_p
	and	f.nr_seq_result_sae = b.nr_seq_result
	and	f.nr_seq_regra = c.nr_sequencia;

	select 	max(x.vl_result)
	into STRICT	ie_retorno_w
	from	pe_result_item x,
		pe_regra_result_escala f
	where	x.nr_sequencia = nr_seq_result_escala_w;



	return ie_retorno_w;
	end;

begin

select	max(a.cd_prescritor),
	max(a.nr_atendimento)
into STRICT	cd_profissional_w,
	nr_atendimento_w
from	pe_prescricao a,
	pe_prescr_item_result b
where	a.nr_sequencia = b.nr_seq_prescr
and	a.nr_sequencia = nr_sequencia_p;

select	count(*)
into STRICT	qt_reg_w
from 	pe_prescricao a,
	pe_prescr_item_result b,
	pe_regra_item_escala c,
	pe_regra_result_escala d
where	a.nr_sequencia	= b.nr_seq_prescr
and	b.nr_seq_result = d.nr_seq_result_sae
and	b.nr_seq_item	= c.nr_seq_item_sae
and	a.nr_sequencia	= nr_sequencia_p
and	(c.nr_seq_item_escala IS NOT NULL AND c.nr_seq_item_escala::text <> '')
and 	c.ie_escala = 79;

if (qt_reg_w > 0) then
	begin
		ie_banho_w		:= obter_valor_escala(19);
		ie_vestir_w		:= obter_valor_escala(20);
		ie_higiene_pessoal_w	:= obter_valor_escala(21);
		ie_transferencia_w	:= obter_valor_escala(22);
		ie_continencia_w	:= obter_valor_escala(23);
		ie_alimentacao_w	:= obter_valor_escala(24);
	end;

	insert into escala_katz(nr_sequencia,
				cd_profissional,
				dt_atualizacao,
				dt_avaliacao,
				ie_banho,
				ie_vestir,
				ie_higiene_pessoal,
				ie_transferencia,
				ie_continencia,
				ie_alimentacao,
				nm_usuario,
				nr_atendimento,
				ie_situacao,
				dt_liberacao)
	values (	nextval('escala_katz_seq'),
				cd_profissional_w,
				clock_timestamp(),
				clock_timestamp(),
				coalesce(ie_banho_w,0),
				coalesce(ie_vestir_w,0),
				coalesce(ie_higiene_pessoal_w,0),
				coalesce(ie_transferencia_w,0),
				coalesce(ie_continencia_w,0),
				coalesce(ie_alimentacao_w,0),
				nm_usuario_p,
				nr_atendimento_w,
				'A',
				clock_timestamp());

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
