CREATE OR REPLACE PROCEDURE gerar_kits_medic_prescr_manual ( nr_prescricao_p bigint, ds_lista_sequencia_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_lista_sequencia_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_sequencia_w		bigint;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (ds_lista_sequencia_p IS NOT NULL AND ds_lista_sequencia_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	ds_lista_sequencia_w	:= ds_lista_sequencia_p;
	 
	while(ds_lista_sequencia_w IS NOT NULL AND ds_lista_sequencia_w::text <> '') loop 
		begin 
		nr_pos_virgula_w	:= position(',' in ds_lista_sequencia_w);
		 
		if (nr_pos_virgula_w > 0) then 
			begin 
			nr_sequencia_w		:= substr(ds_lista_sequencia_w,0,nr_pos_virgula_w-1);
			ds_lista_sequencia_w	:= substr(ds_lista_sequencia_w,nr_pos_virgula_w+1,length(ds_lista_sequencia_w));
			end;
		else 
			begin 
			nr_sequencia_w		:= (ds_lista_sequencia_w)::numeric;
			ds_lista_sequencia_w	:= null;
			end;
		end if;
		 
		if (coalesce(nr_sequencia_w,0) > 0) then 
			begin 
			CALL gerar_kit_medic_prescr_manual( 
				cd_estabelecimento_p, 
				nr_prescricao_p, 
				nr_sequencia_w, 
				nm_usuario_p);
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
