CREATE OR REPLACE PROCEDURE gerar_kit_estoque_adic_ped ( cd_estabelecimento_p bigint, nm_usuario_p text, cd_kit_material_p bigint, nr_sequencia_p bigint) AS $body$
DECLARE


cd_kit_material_w	integer;
nr_sequencia_w		bigint;
nr_seq_kit_estoque_w	bigint;
nr_seq_pac_pedido_w	bigint;
nr_seq_reg_kit_w	bigint;
cd_local_estoque_w	bigint;

c_pedido CURSOR FOR
	SELECT 	nr_sequencia
	from 	agenda_pac_pedido
	where 	nr_seq_agenda = nr_sequencia_p;


BEGIN

open c_pedido;
loop
fetch c_pedido into
	nr_seq_pac_pedido_w;
EXIT WHEN NOT FOUND; /* apply on c_pedido */

	begin

	select 	nr_sequencia
	into STRICT	nr_seq_reg_kit_w
	from 	kit_estoque_reg
	where 	nr_seq_pedido_agenda = nr_seq_pac_pedido_w;

	select 	coalesce(max(cd_local_estoque),0)
	into STRICT	cd_local_estoque_w
	from	kit_estoque
	where	cd_kit_material = cd_kit_material_p
	and	nr_seq_reg_kit = nr_seq_reg_kit_w;

	select	nextval('kit_estoque_seq')
	into STRICT	nr_seq_kit_estoque_w
	;

	insert 	into kit_estoque(
			nr_sequencia,
			cd_kit_material,
			dt_atualizacao,
			nm_usuario,
			dt_montagem,
			nm_usuario_montagem,
			dt_utilizacao,
			nm_usuario_util,
			nr_cirurgia,
			cd_estabelecimento,
			cd_medico,
			nr_prescricao,
			nr_atendimento,
			cd_local_estoque,
			nr_seq_reg_kit,
			ie_status)
	values (	nr_seq_kit_estoque_w,
			cd_kit_material_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			null,
			null,
			null,
			cd_estabelecimento_p,
			null,
			null,
			null,
			CASE WHEN cd_local_estoque_w=0 THEN ''  ELSE cd_local_estoque_w END ,
			nr_seq_reg_kit_w,
			null);

	end;
end loop;
close c_pedido;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
