CREATE OR REPLACE PROCEDURE gerar_kit_estoque_agrup ( nr_sequencia_p bigint, nr_cirurgia_p bigint, nr_prescricao_p bigint, ie_origem_inf_p text, cd_local_estoque_p bigint, cd_perfil_p bigint, cd_medico_p text, nr_doc_interno_p bigint, nm_usuario_p text, nr_doc_interno_aux_p bigint, ie_tipo_doc_interno_p text, ie_tipo_doc_interno_aux_p text) AS $body$
DECLARE

									 
nr_seq_kit_estoque_w	bigint;	
qt_existe_w		bigint;

C01 CURSOR FOR 
	SELECT	b.nr_sequencia 
	from 	kit_estoque_reg a, 
		kit_estoque b	 
	where	a.nr_sequencia = b.nr_seq_reg_kit 
	and	a.nr_sequencia = nr_sequencia_p;


BEGIN 
open C01;
loop 
fetch C01 into 
	nr_seq_kit_estoque_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL Gerar_Prescricao_Cirurgia(	null, 
				nr_seq_kit_estoque_w, 
				nr_cirurgia_p, 
				0, 
				ie_origem_inf_p, 
				nm_usuario_p, 
				nr_prescricao_p, 
				nr_doc_interno_p, 
				cd_local_estoque_p, 
				900, 
				cd_perfil_p, 
				cd_medico_p, 
				nr_doc_interno_aux_p, 
				ie_tipo_doc_interno_p, 
				ie_tipo_doc_interno_aux_p);
	end;
end loop;
close C01;
 
select	count(*) 
into STRICT	qt_existe_w 
from	kit_estoque 
where	nr_seq_reg_kit = nr_sequencia_p 
and	(dt_utilizacao IS NOT NULL AND dt_utilizacao::text <> '');
 
if (qt_existe_w > 0) then 
	update	kit_estoque_reg 
	set	dt_utilizacao 	= clock_timestamp(), 
		nm_usuario_util	= nm_usuario_p	 
	where	nr_sequencia 	= nr_sequencia_p;
end if;
 
commit;
 
end;
$body$
