CREATE OR REPLACE PROCEDURE gerar_kit_prescricao ( cd_estabelecimento_p bigint, nr_prescricao_p bigint, nr_seq_item_p bigint, nm_usuario_p text, ie_manual_p text default 'N', ie_subst_medicamento_p text default 'N') AS $body$
DECLARE


ie_gerar_kit_w	char(1);


BEGIN

select	coalesce(max(ie_kit_prescr_cir), 'N')
into STRICT	ie_gerar_kit_w
from	parametro_estoque
where	cd_estabelecimento	= cd_estabelecimento_p;

if (ie_gerar_kit_w = 'N') then
	select	coalesce(max('N'),'S')
	into STRICT	ie_gerar_kit_w
	from	cirurgia where		nr_prescricao = nr_prescricao_p LIMIT 1;
end if;

if (ie_gerar_kit_w = 'S') then
	CALL Gerar_Kit_Medic_Prescricao(cd_estabelecimento_p, nr_prescricao_p, nr_seq_item_p, nm_usuario_p,'N', ie_manual_p, ie_subst_medicamento_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
