CREATE OR REPLACE PROCEDURE gerar_lab_etiqueta_impressao ( nr_prescricao_p bigint, xml_p text, cd_perfil_p bigint, nm_usuario_p text) AS $body$
DECLARE




nr_seq_lab_etiqueta_conting_w		bigint;
nr_seq_relatorio_w			bigint;
ds_caminho_w				varchar(255);


BEGIN


if	not(coalesce(nr_prescricao_p::text, '') = '') then

	select	max(nr_seq_relatorio)
	into STRICT	nr_seq_relatorio_w
	from	lab_relatorio_imp
	where	cd_perfil = cd_perfil_p;


	select	max(i.ds_caminho_impressora)
	into STRICT	ds_caminho_w
	from	etiqueta e,
		impressora_etiqueta i
	where	nr_seq_relatorio  = nr_seq_relatorio_w
	and	i.nr_sequencia 	  = e.nr_seq_impressora;


	select	nextval('lab_etiqueta_contingencia_seq')
	into STRICT	nr_seq_lab_etiqueta_conting_w
	;


	insert	into lab_etiqueta_contingencia(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_prescricao,
		xml_envio,
		ds_caminho
		)
	values (
		nr_seq_lab_etiqueta_conting_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_prescricao_p,
		xml_p,
		ds_caminho_w
		);

	commit;

end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
