cd_acao_w			varchar(1);
ie_atualiza_kit_w			varchar(1);
ie_consignado_w			varchar(1):= '2';/* '0' = Nao Consignado        '1' = Consignado        '2' = Ambos  */
nr_seq_proc_princ_w		bigint;/*Geliard OS276145*/
dt_atendimento_w		timestamp;
cd_tipo_baixa_w			smallint;
ds_dia_ciclo_w			varchar(255);
