c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.cd_setor_atendimento
	from	regra_lanc_automatico a
	where	a.cd_estabelecimento					= cd_estabelecimento_w
	and	coalesce(a.cd_convenio, cd_convenio_w)			= cd_convenio_w
	and	coalesce(a.ie_tipo_convenio, ie_tipo_convenio_w)		= ie_tipo_convenio_w
	and (coalesce(a.ie_tipo_atendimento,ie_tipo_atendimento_w)	= ie_tipo_atendimento_w	or coalesce(ie_tipo_atendimento_w::text, '') = '')
	and (a.nr_seq_evento					= nr_seq_evento_p)
	and (coalesce(a.cd_material,cd_mat_matpaci_w)			= cd_mat_matpaci_w 	or coalesce(cd_mat_matpaci_w::text, '') = '')
	and (coalesce(a.cd_grupo_material,cd_grupo_mat_w)		= cd_grupo_mat_w 	or coalesce(cd_grupo_mat_w::text, '') = '')
	and (coalesce(a.cd_subgrupo_material,cd_subgrupo_mat_w) 		= cd_subgrupo_mat_w 	or coalesce(cd_subgrupo_mat_w::text, '') = '')
	and (coalesce(a.cd_classe_material,cd_classe_mat_w)		= cd_classe_mat_w 	or coalesce(cd_classe_mat_w::text, '') = '')
	and	coalesce(a.nr_seq_familia,coalesce(nr_seq_familia_w,0))		= coalesce(nr_seq_familia_w,0)
	and	coalesce(a.cd_categoria, cd_categoria_w)			= cd_categoria_w
	and	((coalesce(ie_clinica::text, '') = '') or (ie_clinica = ie_clinica_w))
	and	((coalesce(cd_tipo_acomodacao::text, '') = '') or (coalesce(cd_tipo_acomodacao_w::text, '') = '') or (cd_tipo_acomodacao = cd_tipo_acomodacao_w))
	and	((coalesce(nr_seq_classificacao::text, '') = '') or (coalesce(nr_seq_classificacao_w::text, '') = '') or (nr_seq_classificacao = nr_seq_classificacao_w))
	and (coalesce(ie_via_aplicacao,ie_via_aplicacao_w) = ie_via_aplicacao_w)
	and	((nr_anos_w = 999) or (nr_anos_w between coalesce(qt_ano_min,0) and coalesce(qt_ano_max,999)))
	and	((coalesce(a.cd_funcao::text, '') = '') or (cd_funcao_w = 0) or (a.cd_funcao = cd_funcao_w))
	and	ie_situacao						= 'A'
	and	dt_atual_trunc_w between coalesce(dt_inicio_vigencia,dt_atual_trunc_w) and ESTABLISHMENT_TIMEZONE_UTILS.endOfDay(coalesce(dt_final_vigencia,dt_atual_trunc_w))
	and (((coalesce(ie_consignado::text, '') = '') or (ie_consignado = '2')) or (ie_consignado = ie_consignado_w))
	and	((coalesce(a.cd_tipo_baixa::text, '') = '') or (coalesce(a.cd_tipo_baixa,cd_tipo_baixa_w) = cd_tipo_baixa_w))
	and	((Obter_Se_Contido_char(ds_dia_ciclo_w,ds_dia_ciclo) = 'S') or (coalesce(ds_dia_ciclo,coalesce(ds_dia_ciclo_w,'0')) = coalesce(ds_dia_ciclo_w,'0')))
	and	coalesce(qt_idade_w,1) between coalesce(obter_idade_lancto_auto(a.nr_sequencia,'MIN'),0) and coalesce(obter_idade_lancto_auto(a.nr_sequencia,'MAX'),9999999)
	order by
		coalesce(a.cd_setor_atendimento,0),
		coalesce(a.ie_tipo_atendimento,0),
		coalesce(a.cd_convenio,0),
		coalesce(a.ie_tipo_convenio,0),
		coalesce(a.cd_material,0),
		coalesce(a.nr_seq_familia,0),
		coalesce(a.cd_grupo_material,0),
		coalesce(a.cd_subgrupo_material,0),
		coalesce(a.cd_classe_material,0),
		coalesce(a.ie_clinica,0),
		coalesce(a.cd_funcao,0),
		coalesce(a.cd_tipo_baixa,0);

c02 CURSOR FOR
	SELECT	a.cd_procedimento,
		a.ie_origem_proced,
		a.nr_seq_exame,
		a.cd_material,
		substr(obter_dados_material_estab(a.cd_material,cd_estabelecimento_w,'UMS'),1,30) cd_unidade_medida_consumo,
		CASE WHEN a.ie_quantidade='I' THEN  coalesce(qt_lancamento,1)  ELSE coalesce(qt_lancamento,1) * qt_material_w END ,
		coalesce(ie_medico_atendimento,'N'),
		coalesce(ie_local_estoque,'N'),
		CASE WHEN ie_forma_func_medico='P' THEN coalesce(a.ie_funcao_medico, ie_funcao_medico_w)  ELSE a.ie_funcao_medico END ,		
		coalesce(a.tx_procedimento,100),
		coalesce(a.ie_regra_guia,'RC'),
		coalesce(a.ie_consiste_item,'N'),
    	coalesce(a.ie_consiste_proc_int,'N'),
		coalesce(a.nr_seq_proc_interno, 0),
		a.nr_seq_lanc,
		coalesce(ie_proc_princ_atend, 'N'),
		a.nr_seq_proc_interno,
		a.cd_cnpj_prestador,
		a.cd_cid_doenca,
		coalesce(a.ie_gera_se_item_exc_conta,'S'),
		a.cd_medico
	from 	material		b,
		regra_lanc_aut_pac	a
	where	a.nr_seq_regra  	= nr_seq_regra_w
	and	nr_seq_regra_w 		<> 0
	and	b.ie_situacao 		= 'A'
	and 	coalesce(a.ie_adic_orcamento,'N') = 'N'
	and	(a.cd_material IS NOT NULL AND a.cd_material::text <> '')
	and	a.cd_material 		= b.cd_material
	and	((coalesce(ie_retorno,'N') = 'S') or (coalesce(nr_atend_original_w::text, '') = ''))
	and	((nr_anos_w = 999) or (nr_anos_w between coalesce(qt_ano_min,0) and coalesce(qt_ano_max,999)))
	and	((coalesce(a.ie_acao,'T') = 'T') or (a.ie_acao = cd_acao_w))
	
union all

	SELECT	cd_procedimento,
		ie_origem_proced,
		nr_seq_exame,
		cd_material,
		'XX',
		CASE WHEN ie_quantidade='I' THEN  coalesce(qt_lancamento,1)  ELSE coalesce(qt_lancamento,1) * qt_material_w END ,
		coalesce(ie_medico_atendimento,'N'),
		coalesce(ie_local_estoque,'N'),
		CASE WHEN ie_forma_func_medico='P' THEN coalesce(ie_funcao_medico, ie_funcao_medico_w)  ELSE ie_funcao_medico END ,
		coalesce(tx_procedimento,100),
		coalesce(ie_regra_guia,'RC'),
		coalesce(ie_consiste_item,'N'),
    	coalesce(ie_consiste_proc_int,'N'),
		coalesce(nr_seq_proc_interno, 0),
		nr_seq_lanc,
		coalesce(ie_proc_princ_atend, 'N'),
		nr_seq_proc_interno,
		cd_cnpj_prestador,
		cd_cid_doenca,
		coalesce(ie_gera_se_item_exc_conta,'S'),
		cd_medico
	from 	regra_lanc_aut_pac
	where	nr_seq_regra  	= nr_seq_regra_w
	and	nr_seq_regra_w <> 0
	and 	coalesce(ie_adic_orcamento,'N') = 'N'
	and	((coalesce(ie_retorno,'N') = 'S') or (coalesce(nr_atend_original_w::text, '') = ''))
	and	((cd_procedimento IS NOT NULL AND cd_procedimento::text <> '' AND ie_origem_proced IS NOT NULL AND ie_origem_proced::text <> '') or (nr_seq_proc_interno IS NOT NULL AND nr_seq_proc_interno::text <> ''))
	and	((nr_anos_w = 999) or (nr_anos_w between coalesce(qt_ano_min,0) and coalesce(qt_ano_max,999)))
	and	((coalesce(ie_acao,'T') = 'T') or (ie_acao = cd_acao_w));


BEGIN

nr_seq_matpaci_w	:= nr_sequencia_p;
nr_seq_regra_w		:= 0;
cd_tipo_acomodacao_w	:= null;

ie_medico_profissional_w 	:= obter_valor_param_usuario(281,519,obter_perfil_ativo,nm_usuario_p,0);

begin
select	a.ie_tipo_atendimento,
	a.nr_atend_original,
	a.cd_estabelecimento,
	b.cd_cgc,
	a.cd_setor_atendimento,
	a.cd_medico_resp,
	a.cd_convenio,
	c.ie_tipo_convenio,
	a.cd_categoria,
	a.dt_entrada_unidade,
	a.dt_entrada,
	CASE WHEN obter_valor_conv_estab(c.cd_convenio, b.cd_estabelecimento, 'IE_DOC_CONVENIO')='N' THEN  null WHEN obter_valor_conv_estab(c.cd_convenio, b.cd_estabelecimento, 'IE_DOC_CONVENIO')='S' THEN  coalesce(a.cd_senha, a.nr_doc_convenio)  ELSE a.nr_doc_convenio END ,

/* edgar 

06/10/2004 , os 11672 */
	a.dt_alta,
	a.nr_seq_atepacu,
	a.cd_senha,
	a.ie_clinica,
	a.cd_tipo_acomod_unid,
	coalesce(nr_anos,999),
	a.nr_seq_classificacao,
	a.cd_pessoa_fisica,
	coalesce(a.cd_plano_convenio,'0')
into STRICT	ie_tipo_atendimento_w,
	nr_atend_original_w,
	cd_estabelecimento_w,
	cd_cgc_w,
	cd_setor_atendimento_w,
	cd_medico_resp_w,
	cd_convenio_w,
	ie_tipo_convenio_w,
	cd_categoria_w,
	dt_entrada_unidade_w,
	dt_entrada_w,
	nr_doc_convenio_w,
	dt_alta_w,
	nr_seq_atepacu_w,
	cd_senha_w,
	ie_clinica_w,
	cd_tipo_acomodacao_w,
	nr_anos_w,
	nr_seq_classificacao_w,
	cd_pessoa_fisica_w,
	cd_plano_convenio_w
from	estabelecimento 	b,
	convenio 		c,
	atendimento_paciente_v	a
where	a.cd_estabelecimento	= b.cd_estabelecimento
and	c.cd_convenio		= a.cd_convenio
and	a.nr_atendimento	= nr_atendimento_p;
exception
	when others then
		cd_estabelecimento_w := 9999;
end;

select	coalesce(max(cd_funcao_medico),0),
	coalesce(max(ie_atualiza_kit),'N'),
	coalesce(max(ie_regra_uso_rla),'N'),
	coalesce(max(cd_motivo_exc_conta),12),
	coalesce(max(ie_vincula_cirur_lanc),'N'),
	coalesce(max(ie_vincula_cirur_lanc_mat),'N')
into STRICT	ie_funcao_medico_w,
	ie_atualiza_kit_w,
	ie_regra_uso_w,
	cd_motivo_exc_conta_w,
	ie_vincula_cirur_lanc_w,
	ie_vincula_cirur_lanc_mat_w
from	parametro_faturamento
where	cd_estabelecimento	= cd_estabelecimento_w;




if (nr_seq_evento_p = 441) then
	select	coalesce(max(ds_dia_ciclo),'0')
	into STRICT	ds_dia_ciclo_w
	from	paciente_atendimento
	where	nr_seq_atendimento = ds_adicional_1_p;
end if;

begin
select	max(obter_idade(b.dt_nascimento,coalesce(b.dt_obito,clock_timestamp()),'DIA'))	
into STRICT	qt_idade_w
from	pessoa_fisica b
where	b.cd_pessoa_fisica = cd_pessoa_fisica_w;
exception
when others then
	qt_idade_w := 0;
end;

select 	max(cd_medico_referido)
into STRICT	cd_medico_referido_w
from 	atendimento_paciente
where 	nr_atendimento = nr_atendimento_p;

if (coalesce(nr_seq_matpaci_w,0) > 0) then
	begin
	/* buscar dados do material executado */

	begin
	select	a.cd_material,
		b.cd_grupo_material,
		b.cd_subgrupo_material,
		b.cd_classe_material,
		a.cd_setor_atendimento,
		a.qt_material,
		a.nr_doc_convenio,
		a.nr_prescricao,
		coalesce(a.ie_via_aplicacao,'0'),
		a.nr_interno_conta,
		a.dt_entrada_unidade,
		a.nr_seq_atepacu,
		CASE WHEN a.cd_acao=1 THEN 'L' WHEN a.cd_acao=2 THEN 'D'  ELSE 'T' END ,
		coalesce(b.ie_consignado,'2'),
		a.dt_atendimento,
		coalesce(a.nr_seq_tipo_baixa,0),
		a.NR_DOC_INTERNO,
		a.cd_medico_prescritor,
		b.nr_seq_familia,
		a.nr_cirurgia 
	into STRICT	cd_mat_matpaci_w,
		cd_grupo_mat_w,
		cd_subgrupo_mat_w,
		cd_classe_mat_w,
		cd_setor_atendimento_w,
		qt_material_w,
		nr_doc_convenio_w,
		nr_prescricao_w,
		ie_via_aplicacao_w,
		nr_interno_conta_w,
		dt_entrada_unidade_w,
		nr_seq_atepacu_w,
		cd_acao_w,
		ie_consignado_w,
		dt_atendimento_w,
		cd_tipo_baixa_w,
		NR_DOC_INTERNO_w,
		cd_medico_propaci_w,
		nr_seq_familia_w, 
		nr_cirurgia_w
	from	material_atend_paciente	a,
		estrutura_material_v	b
	where	a.nr_sequencia	= nr_seq_matpaci_w
	and	a.cd_material	= b.cd_material;
	exception
		when others then
		cd_mat_matpaci_w	:= null;
	end;
	end;
end if;

dt_execucao_w	:= coalesce(dt_execucao_w, dt_atendimento_w);

nr_seq_regra_w	:= 0;

select	coalesce(max(obter_funcao_ativa),0)
into STRICT	cd_funcao_w
;


if (coalesce(cd_mat_matpaci_w,0)	<> 0) then
	open c01;
	loop
	fetch c01 into
		nr_sequencia_w,
		cd_setor_regra_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (cd_setor_regra_w IS NOT NULL AND cd_setor_regra_w::text <> '') then
			if (cd_setor_regra_w = cd_setor_atendimento_w) then
				nr_seq_regra_w	:= nr_sequencia_w;
			end if;
		else
			nr_seq_regra_w	:= nr_sequencia_w;
		end if;
		end;
	end loop;
	close c01;
end if;
	
RAISE NOTICE '%', nr_seq_regra_w;

open c02;
loop
fetch c02 into
	cd_procedimento_w,
	ie_origem_proced_w,
	nr_seq_exame_w,
	cd_material_w,
	cd_unid_medida_w,
	qt_lancamento_w,
	ie_medico_atendimento_w,
	ie_local_estoque_w,
	ie_funcao_medico_w,
	tx_procedimento_w,
	ie_regra_guia_w,
	ie_consiste_item_w,
  	ie_consiste_proc_int_w,
	nr_seq_lanc_proc_interno_w,
	nr_seq_acao_regra_w,
	ie_proc_princ_atend_w,
	nr_seq_proc_interno_w,
	cd_cnpj_prestador_w,
	cd_cid_doenca_w,
	ie_gera_se_item_exc_conta_w,
	cd_medico_lanc_aut_pac_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
	begin


   /*tratar para ir para o final caso o proc interno da acao da regra esteja inativo*/

    if (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') then
			select	coalesce(max(ie_situacao),'A')
			into STRICT	ie_situacao_w
			from	proc_interno
			where	nr_sequencia = nr_seq_proc_interno_w;

			if (ie_situacao_w = 'I') then
				goto continue_loop;
			end if;
    end if;

	if (coalesce(nr_seq_proc_interno_w,0) > 0) then
