				elsif (ie_acao_excesso_w = 'P') then
				
					if (qt_excedida_w   >= qt_lancamento_w) then
						
						SELECT * FROM obter_convenio_particular_pf(cd_estabelecimento_w, cd_convenio_w, '', coalesce(dt_execucao_w, coalesce(dt_alta_w,clock_timestamp())), cd_convenio_glosa_w, cd_categoria_glosa_w) INTO STRICT cd_convenio_glosa_w, cd_categoria_glosa_w;
						
						cd_convenio_w	:= cd_convenio_glosa_w;
						cd_categoria_w	:= cd_categoria_glosa_w;
						
						
					else
					
						qt_lancamento_w := qt_lancamento_w - qt_excedida_w;
						
						SELECT * FROM obter_convenio_particular_pf(cd_estabelecimento_w, cd_convenio_w, '', coalesce(dt_execucao_w, coalesce(dt_alta_w,clock_timestamp())), cd_convenio_glosa_w, cd_categoria_glosa_w) INTO STRICT cd_convenio_glosa_w, cd_categoria_glosa_w;
						
						nr_seq_excedido_w := Inserir_Material_Atend_Pac(nr_atendimento_p, null, cd_material_w, coalesce(dt_execucao_w, coalesce(dt_alta_w,clock_timestamp())), cd_convenio_glosa_w, cd_categoria_glosa_w, nr_seq_atepacu_w, nm_usuario_p, qt_excedida_w, cd_local_estoque_w, '1', 'N', nr_seq_excedido_w, null, null);
										
						CALL atualiza_preco_material(nr_seq_excedido_w, nm_usuario_p);
						
						CALL Ajustar_Conta_Vazia(nr_atendimento_p, nm_usuario_p);
						
					end if;	
				end if;
				
			end if;
			
			if (coalesce(cd_kit_material_w, 0) = 0) or
				((coalesce(cd_kit_material_w, 0) > 0) and (ie_material_pai_w = 'S')) then
				
				if (nr_seq_evento_p = 132) and (ds_adicional_1_p IS NOT NULL AND ds_adicional_1_p::text <> '') then
					dt_execucao_w	:= to_date(ds_adicional_1_p,'dd/mm/yyyy hh24:mi:ss');
				end if;
							
				select	nextval('material_atend_paciente_seq')
				into STRICT 	nr_sequencia_w
				;
				begin
				insert into material_atend_paciente(
					nr_sequencia,
					nr_atendimento,
					dt_entrada_unidade,
					cd_material,
					cd_material_exec,
					dt_conta,
					dt_atendimento,
					qt_material,
					qt_executada,
					dt_atualizacao,
					nm_usuario,
					cd_unidade_medida,
					cd_convenio,
					cd_categoria,
					cd_acao,
					cd_local_estoque,
					cd_setor_atendimento,
					ie_valor_informado,
					nm_usuario_original,
					cd_setor_receita,
					cd_situacao_glosa,
					nr_doc_convenio,
					nr_seq_proc_princ,
					cd_proced_referencia,
					nr_seq_atepacu,
					ie_auditoria,
					ds_observacao,
					nr_seq_regra_lanc,
					cd_cgc_prestador,
					NR_DOC_INTERNO,
					nr_cirurgia)
				values (
					nr_sequencia_w,
					nr_atendimento_p,
					dt_entrada_unidade_w,
					cd_material_w,
					cd_material_w,
					coalesce(dt_execucao_w, coalesce(dt_alta_w,clock_timestamp())),
					coalesce(dt_execucao_w, coalesce(dt_alta_w,clock_timestamp())),
	 				qt_lancamento_w,
					qt_lancamento_w,
					clock_timestamp(),
					nm_usuario_p,
					cd_unid_medida_w,
					cd_convenio_w,
					cd_categoria_w,
					'1',
					cd_local_estoque_w,
					cd_setor_atendimento_w,
					'N',
					nm_usuario_p,
					cd_setor_atendimento_w,
					0,
					nr_doc_convenio_w,
					null,
					null,
					nr_seq_atepacu_w,
					'N',
					--'LA Regra: '  || nr_seq_regra_w || ' Evento: ' || nr_seq_evento_p,
					wheb_mensagem_pck.get_texto(304246, 'NR_SEQ_REGRA=' || nr_seq_regra_w || ';' || 'NR_SEQ_EVENTO=' || nr_seq_evento_p),
					nr_seq_regra_w,
					cd_cnpj_prestador_w,
					NR_DOC_INTERNO_w,                     
			        CASE WHEN nr_seq_evento_p=132 THEN CASE WHEN ie_vincula_cirur_lanc_mat_w='S' THEN  nr_cirurgia_w  ELSE null END   ELSE null END );
				exception
					when 	others then
					nr_sequencia_w := 0;
				end;				
				
				if (coalesce(ie_atualiza_kit_w,'N') = 'S') and
					((coalesce(cd_kit_material_w, 0) > 0) and (ie_material_pai_w = 'S')) then
					update	material_atend_paciente
					set 	cd_kit_material = cd_kit_material_w
					where 	nr_sequencia = nr_sequencia_w;					
				end if;									
				
			end if;
			
			<<final_mat>>
			
			if (nr_sequencia_w > 0) then
				CALL atualiza_preco_material(nr_sequencia_w, nm_usuario_p);
				CALL gerar_material_vinculado_rla(nr_seq_matpaci_w, nr_sequencia_w, null, nm_usuario_p);
			end if;
			end;
		end if;
		end;
	end if;
	end;
	<<continue_loop>>
	ie_situacao_w := null;
end loop;
close c02;

end;
$body$
