CREATE OR REPLACE PROCEDURE gerar_lanc_automatico_tabela ( nr_atendimento_p bigint, nr_seq_evento_p bigint, nm_tabela_p text, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



nm_tabela_w		varchar(50);
nm_atributo_w		varchar(50);

ds_comando_w		varchar(4000);
ds_retorno_w		varchar(2000);

cd_convenio_w		bigint;
ie_tipo_atendimento_w	bigint;
cd_setor_atendimento_w	bigint;
cd_estabelecimento_w	bigint;

C01 CURSOR FOR
	SELECT	nm_tabela,
		nm_atributo
	from	REGRA_LANC_AUTOMATICO a
	where	nr_seq_evento	= nr_seq_evento_p
	and	nm_tabela	= nm_tabela_p
	and	(nm_atributo IS NOT NULL AND nm_atributo::text <> '')
	and	ie_situacao = 'A'
	and	a.cd_estabelecimento = cd_estabelecimento_w
	and	coalesce(a.cd_convenio, cd_convenio_w) = cd_convenio_w
	and	coalesce(a.ie_tipo_atendimento,ie_tipo_atendimento_w) = ie_tipo_atendimento_w
	and	coalesce(a.cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w;




BEGIN

select	max(cd_convenio),
	max(ie_tipo_atendimento),
	max(cd_setor_atendimento),
	max(cd_estabelecimento)
into STRICT	cd_convenio_w,
	ie_tipo_atendimento_w,
	cd_setor_atendimento_w,
	cd_estabelecimento_w
from	atendimento_paciente_v
where	nr_atendimento = nr_atendimento_p;

open C01;
loop
fetch C01 into
	nm_tabela_w,
	nm_atributo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_comando_w	:= 	' select '||nm_atributo_w||' '||
				' from	 '||nm_tabela_w|| ' '||
				' where nr_sequencia = :nr_sequencia';

	ds_retorno_w	:= Obter_select_concatenado_bv(ds_comando_w,'nr_sequencia='||nr_sequencia_p||';','');

	if ((trim(both ds_retorno_w) IS NOT NULL AND (trim(both ds_retorno_w))::text <> '')) then
		CALL gerar_lancamento_automatico(nr_atendimento_p,null,nr_seq_evento_p,nm_usuario_p,null,nm_tabela_w,nm_atributo_w,ds_retorno_w,null,null);
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
