		update	cirurgia 
		set	dt_lanc_automatico = clock_timestamp() 
		where	nr_cirurgia = nr_cirurgia_p;
	end if;
	 
end if;	
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
