CREATE OR REPLACE PROCEDURE gerar_laudo_pac_imagem_padrao ( nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_laudo_p bigint, cd_protocolo_p bigint, ds_retorno_arquivo_p INOUT text ) AS $body$
DECLARE

ds_titulo_w		varchar(100);
ds_arquivo_w		varchar(100);
nr_seq_img_w		bigint;
nr_seq_bco_w		bigint;
ds_caminho_arq_w	varchar(100);
ds_extensao_arq_w	varchar(10);

ds_retorno_arquivo_w	varchar(2000) := '';


c01 CURSOR FOR
	SELECT	nr_sequencia,
		ds_titulo,
		ds_arquivo
	from	laudo_paciente_imagem_bco
	where	cd_protocolo = cd_protocolo_p
	order	by ds_titulo;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_bco_w,
	ds_titulo_w,
	ds_arquivo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nextval('laudo_pac_imagem_edicao_seq')
	into STRICT	nr_seq_img_w
	;

	ds_caminho_arq_w := Obter_Valor_Param_Usuario(28, 131, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p);

	ds_extensao_arq_w := substr(ds_arquivo_w, instr(ds_arquivo_w,'.',-1), 10);

	ds_caminho_arq_w := ds_caminho_arq_w||nr_seq_img_w||ds_extensao_arq_w;


	insert into laudo_pac_imagem_edicao(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_arquivo,
		ds_titulo,
		nr_seq_laudo
		)
	values (
		nr_seq_img_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_caminho_arq_w,
		ds_titulo_w,
		nr_seq_laudo_p
		);

	ds_retorno_arquivo_w := ds_retorno_arquivo_w||','||nr_seq_bco_w||'-'||nr_seq_img_w;

	end;
end loop;
close c01;


ds_retorno_arquivo_p := substr(ds_retorno_arquivo_w,2,255);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_laudo_pac_imagem_padrao ( nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_laudo_p bigint, cd_protocolo_p bigint, ds_retorno_arquivo_p INOUT text ) FROM PUBLIC;

