CREATE OR REPLACE PROCEDURE gerar_leitura_item_barras (nr_seq_processo_p bigint, cd_material_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_item_w	bigint;


BEGIN 
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
 
	select	coalesce(max(nr_sequencia),0) 
	into STRICT	nr_seq_item_w 
	from	adep_processo_item 
	where	nr_seq_processo								= nr_seq_processo_p 
	and	(substr(obter_dados_horario(nr_seq_horario,'CI'),1,6))::numeric 	= cd_material_p 
	and	ie_barras									= 'N' 
	and	ie_suspenso									= 'N';
 
	if (nr_seq_item_w > 0) then 
 
		update	adep_processo_item 
		set	ie_barras	= 'S', 
			nm_usuario	= nm_usuario_p 
		where	nr_sequencia	= nr_seq_item_w;
 
	end if;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
