CREATE OR REPLACE PROCEDURE gerar_liberacao_etapas_dia ( cd_estabelecimento_p bigint, dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE



dt_inicio_w	timestamp;
dt_fim_w	timestamp;
nr_seq_etapa_w	bigint;
qt_reg_w	bigint;

C01 CURSOR FOR
	SELECT	b.nr_seq_etapa
	from	checkup a,
		checkup_etapa b
	where	a.nr_sequencia	= b.nr_seq_checkup
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and	a.dt_previsto between dt_inicio_w and dt_fim_w
	group by b.nr_seq_etapa;


BEGIN
dt_inicio_w	:= trunc(dt_referencia_p);
dt_fim_w	:= fim_dia(dt_inicio_w);

open C01;
loop
fetch C01 into
	nr_seq_etapa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_reg_w
	from	etapa_checkup_lib_dia a
	where	a.cd_estabelecimento	= cd_estabelecimento_p
	and	a.nr_seq_etapa		= nr_seq_etapa_w
	and	a.dt_referencia	 between dt_inicio_w and dt_fim_w;

	if (qt_reg_w	= 0) then

		insert into etapa_checkup_lib_dia(	nr_sequencia,
							cd_estabelecimento,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_seq_etapa,
							dt_referencia,
							ie_liberado,
							dt_liberacao)
				values (	nextval('etapa_checkup_lib_dia_seq'),
							cd_estabelecimento_p,
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							nr_seq_etapa_w,
							trunc(dt_referencia_p),
							'N',
							null);

	end if;

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
