SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_lib_etapa_acesso ( dt_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

cd_pessoa_fisica_w	varchar(10);
qt_reg_w		bigint;
dt_inicio_w		timestamp;
dt_fim_w		timestamp;
nr_seq_etapa_w		bigint;


C01 CURSOR FOR
	SELECT	nr_seq_etapa
	from	etapa_checkup_lib a
	where	a.CD_PESSOA_FISICA	= cd_pessoa_fisica_w;

BEGIN

if (OBTER_VALOR_PARAM_USUARIO(996,30,null,nm_usuario_p,cd_estabelecimento_p)	= 'S') then
	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	usuario
	where	nm_usuario	= nm_usuario_p;

	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
		dt_inicio_w	:= trunc(dt_referencia_p);
		dt_fim_w	:= fim_dia(dt_inicio_w);
		select	count(*)
		into STRICT	qt_reg_w
		from	etapa_checkup_lib_dia
		where	cd_estabelecimento	= cd_estabelecimento_p
		and	dt_referencia between dt_inicio_w and dt_fim_w
		and	ie_liberado = 'N';

		if (qt_reg_w	> 0) then
			open C01;
			loop
			fetch C01 into
				nr_seq_etapa_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin
				update	etapa_checkup_lib_dia
				set	ie_liberado = 'S',
					dt_liberacao = clock_timestamp()
				where		cd_estabelecimento	= cd_estabelecimento_p
				and	dt_referencia between dt_inicio_w and dt_fim_w
				and	nr_seq_etapa	= nr_seq_etapa_w
				and	ie_liberado = 'N';
				commit;
				end;
			end loop;
			close C01;
		end if;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
