CREATE OR REPLACE PROCEDURE gerar_lista_documentacao ( cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
BEGIN

insert into pessoa_documentacao(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	cd_pessoa_fisica,
	nr_seq_documento,
	ie_entregue,
	ie_situacao)
SELECT	nextval('pessoa_documentacao_seq'),
	clock_timestamp(),
	nm_usuario_p,
	cd_pessoa_fisica_p,
	nr_sequencia,
	'N',
	'A'
from	tipo_documentacao
where	ie_situacao	= 'A'
and	ie_finalidade in ('M','A')
and	obter_se_tipo_documentacao_lib(nr_sequencia) = 'S';

commit;

END;
