CREATE OR REPLACE PROCEDURE gerar_lista_espera_pa ( cd_agenda_p bigint, cd_pessoa_fisica_p text, nr_atendimento_p bigint, cd_especialidade_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE

 
