SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_lista_reg_lfpd ( nr_seq_regra_p text) AS $body$
DECLARE


contador_w	bigint := 0;
cd_registro_w	varchar(15);
qt_registros_w	bigint;

c01 CURSOR FOR
	SELECT	vl_dominio
	from	valor_dominio
	where	cd_dominio = 4814
	order by nr_seq_apresent;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	fis_regra_lfpd_reg
where	nr_seq_regra_lfpd = nr_seq_regra_p;

if (qt_registros_w = 0)  then

	open C01;
	loop
	fetch C01 into
		cd_registro_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		contador_w := contador_w + 1;

		insert into fis_regra_lfpd_reg(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_seq_regra_lfpd,
							cd_registro,
							ie_gerar)
					values (	nextval('fis_regra_lfpd_reg_seq'),
							clock_timestamp(),
							'Tasy',
							clock_timestamp(),
							'Tasy',
							nr_seq_regra_p,
							cd_registro_w,
							'S');

		if (mod(contador_w, 100) = 0) then
			commit;
		end if;

		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_lista_reg_lfpd ( nr_seq_regra_p text) FROM PUBLIC;

