CREATE OR REPLACE PROCEDURE gerar_lista_seq_kit_estoque ( cd_pessoa_fisica_p bigint, nr_prescricao_p bigint, cd_local_estoque_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_sequencia_p INOUT text, ie_sucesso_p INOUT text) AS $body$
DECLARE


ds_retorno_w		varchar(2000);
ds_sequencia_w		varchar(2000);
ds_seq_aux		varchar(100);
ie_sucesso_w		varchar(1);
nr_pos_virgula_w	bigint;
nr_seq_sequencia_w	bigint;
ds_retorno_ww		varchar(2000);


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (cd_local_estoque_p IS NOT NULL AND cd_local_estoque_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	ie_sucesso_w := 'N';

	SELECT * FROM gerar_carrinho_kit_estoque(
			cd_estabelecimento_p, cd_pessoa_fisica_p, nm_usuario_p, nr_prescricao_p, cd_local_estoque_p, ds_retorno_w, ds_retorno_ww) INTO STRICT ds_retorno_w, ds_retorno_ww;

	while(ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') loop
		begin

		ie_sucesso_w := 'S';

		nr_pos_virgula_w := position(',' in ds_retorno_w);

		if (nr_pos_virgula_w > 0) then
			begin
			nr_seq_sequencia_w	:= (substr(ds_retorno_w,0,nr_pos_virgula_w-1))::numeric;
			ds_retorno_w		:= substr(ds_retorno_w,nr_pos_virgula_w+1,length(ds_retorno_w));
			end;
		else
			begin
			nr_seq_sequencia_w	:= (ds_retorno_w)::numeric;
			ds_retorno_w		:= null;
			end;
		end if;

		if (nr_seq_sequencia_w > 0) then
			begin

			select	nr_sequencia
			into STRICT	ds_seq_aux
			from    kit_estoque
			where   nr_seq_reg_kit = nr_seq_sequencia_w;

			if (ds_seq_aux IS NOT NULL AND ds_seq_aux::text <> '') then
				begin

				ds_sequencia_w := ds_sequencia_w +  ds_seq_aux + ',';

				end;
			end if;

			end;
		end if;

		end;
	end loop;

	end;
end if;

commit;

ds_sequencia_p	:= ds_sequencia_w;
ie_sucesso_p	:= ie_sucesso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
