CREATE OR REPLACE PROCEDURE gerar_localidade_usuario (NM_USER_P usuario.nm_usuario%TYPE) AS $body$
DECLARE

   C01 CURSOR FOR SELECT
                        a.DS_LOCALE,
                        a.DS_CALENDAR,
                        a.DS_LANGUAGE,
                        a.DS_SECONDARY_CALENDAR
                    FROM
                        REGRA_PADRAO_USUARIO a
                    WHERE
                        a.DT_ATUALIZACAO = ( SELECT
                                                MAX(b.DT_ATUALIZACAO) 
                                            FROM REGRA_PADRAO_USUARIO b 
                                            WHERE ((DS_LOCALE IS NOT NULL AND DS_LOCALE::text <> '') AND 
                                                 (DS_CALENDAR IS NOT NULL AND DS_CALENDAR::text <> '') AND
                                                 (DS_LANGUAGE IS NOT NULL AND DS_LANGUAGE::text <> '') AND
                                                 (DS_SECONDARY_CALENDAR IS NOT NULL AND DS_SECONDARY_CALENDAR::text <> '')) AND IE_SITUACAO = 'A');
   row_cur C01%rowtype;
   i_count bigint := 0;

BEGIN
    SELECT COUNT(1) into STRICT i_count FROM USER_LOCALE a WHERE a.NM_USUARIO = NM_USER_P;
    OPEN C01;
    FETCH C01 INTO row_cur;
        if C01%found AND i_count = 0 then
            INSERT INTO USER_LOCALE(
                NM_USER,
                DS_LOCALE,
                DS_CALENDAR,
                DS_LANGUAGE,
                DS_TIMEZONE,
                DT_ATUALIZACAO,
                NM_USUARIO,
                DT_ATUALIZACAO_NREC,
                NM_USUARIO_NREC,
                DS_SECONDARY_CALENDAR,
                NM_TIMEZONE_DATABASE
            ) VALUES (
                NM_USER_P,
                row_cur.DS_LOCALE,
                row_cur.DS_CALENDAR,
                row_cur.DS_LANGUAGE,
                null,
                clock_timestamp(),
                WHEB_USUARIO_PCK.GET_NM_USUARIO,
                clock_timestamp(),
                WHEB_USUARIO_PCK.GET_NM_USUARIO,
                row_cur.DS_SECONDARY_CALENDAR,
                null
            );
            END IF;
    CLOSE C01;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
