CREATE OR REPLACE PROCEDURE gerar_log_adep_pda ( ds_log_p text, nr_atendimento_p bigint, nr_prescricao_p bigint, cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_horario_p timestamp, ds_etiqueta_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_log_w		bigint;
cd_setor_atendimento_w	integer;
cd_unidade_basica_w	varchar(10);
cd_unidade_compl_w	varchar(10);
cd_pessoa_fisica_w	varchar(10);
				

BEGIN 
 
if (ds_log_p IS NOT NULL AND ds_log_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	select	max(obter_unidade_atendimento(nr_atendimento_p,'A','CS')), 
		max(obter_unidade_atendimento(nr_atendimento_p,'A','UB')), 
		max(obter_unidade_atendimento(nr_atendimento_p,'A','UC')) 
	into STRICT	cd_setor_atendimento_w, 
		cd_unidade_basica_w, 
		cd_unidade_compl_w 
	;
	 
	select	nextval('adep_log_pda_seq') 
	into STRICT	nr_seq_log_w 
	;
	 
	insert into adep_log_pda(	 
			nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			cd_pessoa_fisica, 
			nr_atendimento, 
			dt_horario, 
			nr_prescricao, 
			ds_etiqueta, 
			cd_setor_atendimento, 
			cd_unidade_basica, 
			cd_unidade_compl, 
			cd_material, 
			cd_procedimento, 
			ie_origem_proced, 
			ds_log) 
		values (	nr_seq_log_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			cd_pessoa_fisica_w, 
			nr_atendimento_p, 
			dt_horario_p, 
			nr_prescricao_p, 
			ds_etiqueta_p, 
			cd_setor_atendimento_w, 
			cd_unidade_basica_w, 
			cd_unidade_compl_w, 
			cd_material_p, 
			cd_procedimento_p, 
			ie_origem_proced_p, 
			ds_log_p);
	 
end if;
 
commit;
 
end;
$body$
