CREATE OR REPLACE PROCEDURE gerar_log_avaliacao_paciente ( nr_seq_avaliacao_p bigint, nm_usuario_p text) AS $body$
DECLARE

qt_itens_w	integer;

BEGIN
 
select	count(*) 
into STRICT	qt_itens_w 
from	Med_Avaliacao_Paciente 
where	nr_sequencia = nr_seq_avaliacao_p;
 
 
if (qt_itens_w > 0) and (nr_seq_avaliacao_p > 0) and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	 
	insert into	med_avaliacao_paciente_log( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_seq_avaliacao 
		) 
	values ( 
		nextval('med_avaliacao_paciente_log_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_seq_avaliacao_p 
		);
	 
end if;
 
commit;
 
end;
$body$
