CREATE OR REPLACE PROCEDURE gerar_log_conexa_xml (cd_agendamento_externo_p text, cd_unidade_solic_p bigint, id_age_consulta_p bigint, dt_agenda_p timestamp, id_especialidade_conexa_p text, id_profissional_p text, ds_log_p text, nm_usuario_p text) AS $body$
DECLARE


ds_especialidade_w	varchar(100);
nm_profissional_w	varchar(255);
cd_estabelecimento_w estabelecimento.cd_estabelecimento%type;
			

BEGIN

select wheb_usuario_pck.get_cd_estabelecimento
into STRICT cd_estabelecimento_w
;

select	max(ds_especialidade)
into STRICT	ds_especialidade_w
from	especialidade_medica
where	cd_especialidade_externo = id_especialidade_conexa_p;

select	max(substr(obter_nome_pf(a.cd_pessoa_fisica), 1, 255))
into STRICT	nm_profissional_w
from	pessoa_fisica a,
	pf_codigo_externo b
where	b.cd_pessoa_fisica 		= a.cd_pessoa_fisica
and	b.ie_tipo_codigo_externo	= 'CO'
and	b.cd_pessoa_fisica_externo	= id_profissional_p
and (coalesce(b.cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w);

insert into agenda_log_conexa_xml(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_agendamento_externo,
				ds_log,
				dt_log,
				cd_unidade_solic,
				cd_agenda_externa,
				dt_agenda,
				ds_especialidade,
				nm_profissional)
			values (	nextval('agenda_log_conexa_xml_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_agendamento_externo_p,
				ds_log_p,
				clock_timestamp(),
				cd_unidade_solic_p,
				id_age_consulta_p,
				dt_agenda_p,
				ds_especialidade_w,
				nm_profissional_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
