CREATE OR REPLACE PROCEDURE gerar_log_escritural (ie_opcao_p text, nr_seq_documento_p bigint, nm_usuario_p text, ds_log_p text, ie_commit_p text) AS $body$
DECLARE


/* NÃO DAR COMMIT NESTA PROCEDURE

ie_opcao_p

C	Cobrança Escritural
P	Pagamento Escritural

*/
nr_sequencia_w		cobranca_escrit_log.nr_sequencia%type;


BEGIN

if (ie_opcao_p	= 'C') then

	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_sequencia_w
	from	cobranca_escrit_log;

	insert	into cobranca_escrit_log(ds_log,
		dt_atualizacao,
		nm_usuario,
		nr_seq_cobranca,
		nr_sequencia)
	values (ds_log_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_documento_p,
		nr_sequencia_w);

elsif (ie_opcao_p	= 'P') then

	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_sequencia_w
	from	banco_escrit_log;

	insert	into banco_escrit_log(ds_log,
		dt_atualizacao,
		nm_usuario,
		nr_seq_banco_escrit,
		nr_sequencia)
	values (ds_log_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_documento_p,
		nr_sequencia_w);

end if;

if (coalesce(ie_commit_p,'S')	= 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
