SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_log_reavaliacao (ie_opcao_p text, nm_usuario_p text, nr_atendimento_p bigint) AS $body$
DECLARE

 
/* 
ie_opcao_p 
ES - 	 Enfermeira Solicita 
EDS - Enfermeira desfaz solicitação 
MI - 	 Médico inicia reavaliacao médica 
MDI - Médico desfaz inicio da reavaliação médica 
MF -	 Médico finaliza a reavaliação médica 
MDF - Médico desfaz a finalização da reavaliação médica 
*/
 
 
dt_reaval_medica_w 		timestamp;
dt_desf_reaval_medica_w timestamp;
dt_inicio_reaval_w 		timestamp;
dt_desf_inicio_reaval_w timestamp;
dt_fim_reaval_w 		timestamp;
dt_desf_fim_reaval_w	timestamp;


BEGIN 
 
if (ie_opcao_p = 'ES') then 
	-- Opção: Reavaliação médica (Enfermeira) 
	 
	update	atendimento_paciente 
	set	dt_inicio_reavaliacao	 = NULL, 
		dt_fim_reavaliacao		 = NULL, 
		nm_usuario   			= nm_usuario_p, 
		dt_atualizacao 			= clock_timestamp() 
	where	nr_atendimento		= nr_atendimento_p;
	 
	commit;
	 
	dt_reaval_medica_w := clock_timestamp();
	 
elsif (ie_opcao_p = 'EDS') then 
	-- Opção: Desfazer reavaliação médica (Enfermeira) 
	dt_desf_reaval_medica_w := clock_timestamp();
elsif (ie_opcao_p = 'MI') then 
	-- Opção: Reavaliação médica 
	CALL Atualizar_datas_pa(nr_atendimento_p,'EFR',nm_usuario_p);
	dt_inicio_reaval_w := clock_timestamp();
elsif (ie_opcao_p = 'MDI') then 
	-- Opção: Desfazer reavaliação médica 
	dt_desf_inicio_reaval_w := clock_timestamp();
elsif (ie_opcao_p = 'MF') then 
	-- Opção: Fim reavaliação 
	dt_fim_reaval_w := clock_timestamp();
elsif (ie_opcao_p = 'MDF') then 
	-- Opção: Fim reavaliação 
	dt_desf_fim_reaval_w := clock_timestamp();
end if;
 
 
insert into reavaliacao_med_log_alt(nr_sequencia, 
			 dt_atualizacao, 
			 nm_usuario, 
			 dt_atualizacao_nrec, 
			 nr_atendimento, 
			 dt_reaval_medica, 
			 dt_desf_reaval_medica, 
			 dt_inicio_reaval, 
			 dt_desf_inicio_reaval, 
			 dt_fim_reaval, 
			 dt_desf_fim_reaval) 
	values (nextval('reavaliacao_med_log_alt_seq') , 
			 clock_timestamp(), 
			 nm_usuario_p, 
			 clock_timestamp(), 
			 nr_atendimento_p, 
			 dt_reaval_medica_w, 
			 dt_desf_reaval_medica_w, 
			 dt_inicio_reaval_w, 
			 dt_desf_inicio_reaval_w, 
			 dt_fim_reaval_w, 
			 dt_desf_fim_reaval_w);
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
