SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_lotes_agrupados ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint, nr_seq_turno_p bigint, nr_seq_classif_p bigint, dt_inicio_p timestamp, dt_final_p timestamp, nm_usuario_p text, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


nr_seq_agrup_w		bigint;
nr_seq_lote_w		bigint;
nr_seq_agrup_item_w 	bigint;
qt_lotes_agrup_w	bigint;
qt_lote_w		bigint;
ie_agrupar_lote_copia_w parametros_farmacia.ie_agrupar_lote_copia%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	regra_turno_disp b,
		ap_lote a
	where	a.nr_seq_turno = b.nr_sequencia
	and	b.nr_sequencia = nr_seq_turno_p
	and	a.cd_setor_atendimento = cd_setor_atendimento_p
	and (nr_seq_classif_p = 0 or a.nr_seq_classif = nr_seq_classif_p)
	and (nr_atendimento_p = 0 or obter_atendimento_prescr(nr_prescricao) = nr_atendimento_p)
	and	a.ie_status_lote = 'A'
	and	trunc(a.dt_atend_lote,'dd') between dt_inicio_p and dt_final_p
    and ((coalesce(a.nr_lote_agrupamento::text, '') = '') or (ie_agrupar_lote_copia_w <> 'S'));


BEGIN

select  coalesce(max(ie_agrupar_lote_copia), 'N')
into STRICT  ie_agrupar_lote_copia_w
from  parametros_farmacia
where cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

select	count(*)
into STRICT	qt_lotes_agrup_w
from	regra_turno_disp b,
	ap_lote a
where	a.nr_seq_turno = b.nr_sequencia
and	b.nr_sequencia = nr_seq_turno_p
and	a.cd_setor_atendimento = cd_setor_atendimento_p
and (nr_seq_classif_p = 0 or a.nr_seq_classif = nr_seq_classif_p)
and (nr_atendimento_p = 0 or obter_atendimento_prescr(nr_prescricao) = nr_atendimento_p)
and	a.ie_status_lote = 'A'
and	trunc(a.dt_atend_lote,'dd') between dt_inicio_p and dt_final_p
and not exists (	SELECT	1
		from	ap_lote_agrup_item c
		where	c.nr_seq_lote = a.nr_sequencia)
and ((exists (select	1
					from	parametros_farmacia x
					where 	x.cd_estabelecimento = b.cd_estabelecimento
					and 	coalesce(x.ie_agrupar_lote_copia,'N') = 'S') and coalesce(a.ie_agrupamento, 'N') <> 'S')
      or
      not exists (select	1 
                    from	parametros_farmacia x
                    where 	x.cd_estabelecimento = b.cd_estabelecimento
                    and 	coalesce(x.ie_agrupar_lote_copia,'N') = 'S'));


if (qt_lotes_agrup_w = '0') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266002);
	--'Nao existem lotes para serem agrupados!'
end if;

if (nr_seq_turno_p = 0) or (cd_setor_atendimento_p = 0) or (nr_seq_classif_p = 0 AND nr_atendimento_p = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266003);
	--'Devem ser informados o Turno e o Setor para geraracao dos lotes agrupados!'
end if;


select	nextval('ap_lote_agrup_seq')
into STRICT	nr_seq_agrup_w
;

insert into ap_lote_agrup(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_setor_atendimento,
	nr_seq_turno,
	nr_seq_classif,
	dt_liberacao)
values (nr_seq_agrup_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_setor_atendimento_p,
	nr_seq_turno_p,
	CASE WHEN nr_seq_classif_p=0 THEN null  ELSE nr_seq_classif_p END ,
	null);

open C01;
loop
fetch C01 into
	nr_seq_lote_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('ap_lote_agrup_item_seq')
	into STRICT	nr_seq_agrup_item_w
	;

	insert into ap_lote_agrup_item(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_agrup,
			nr_seq_lote,
			ie_status)
	values (nr_seq_agrup_item_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_agrup_w,
			nr_seq_lote_w,
			'P');
	end;
end loop;
close C01;

nr_sequencia_p := nr_seq_agrup_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_lotes_agrupados ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint, nr_seq_turno_p bigint, nr_seq_classif_p bigint, dt_inicio_p timestamp, dt_final_p timestamp, nm_usuario_p text, nr_sequencia_p INOUT bigint) FROM PUBLIC;

