-- DATASOURCE: dbi:Oracle:host=srv-dbora-03.whebdc.com.br;service_name=DEV_1815;port=1521

SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_lote_agrupamento ( nr_atendimento_p bigint, cd_pessoal_fisica_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_local_estoque_ant_w		ap_lote.cd_local_estoque%type := 0;
nr_seq_turno_ant_w	 		ap_lote.nr_seq_turno%type := 0;
nr_seq_classif_ant_w		ap_lote.nr_seq_classif%type := 0;
nr_seq_local_geracao_ant_w	ap_lote.nr_seq_local_geracao%type := 'NO';
nr_sequencia_w				ap_lote.nr_sequencia%type;
ds_maq_user_w				varchar(80);
ds_prescricoes_w			varchar(2000);
qt_lotes_agrup_w			bigint;
nr_lote_agrupado_w			bigint;
prescr_w 					bigint;
nr_atendimento_ant_w		bigint := 0;
dt_incio_turno_ant_w		ap_lote.dt_inicio_turno%type := clock_timestamp();
cd_pessoa_fisica_ant_w		ap_lote_agrupamento.cd_pessoa_fisica%type := '0';
nr_prescricao_w				prescr_medica.nr_prescricao%type;
nr_seq_lote_w				ap_lote.nr_sequencia%type;
cd_local_estoque_w			local_estoque.cd_local_estoque%type;
nr_seq_turno_w				regra_turno_disp.nr_sequencia%type;
nr_seq_classif_w			classif_lote_disp_far.nr_sequencia%type;
dt_inicio_turno_w			ap_lote.dt_inicio_turno%type := clock_timestamp();
nr_seq_local_geracao_w		ap_lote.nr_seq_local_geracao%type;
nr_local_geracao_assoc_w	ap_lote.nr_seq_local_geracao%type;
nr_atendimento_w			ap_lote.nr_atendimento%type;
cd_pessoa_fisica_w			ap_lote_agrupamento.cd_pessoa_fisica%type;
qt_cont_w					bigint := 0;
ie_gerar_lote_turno_dia_w	varchar(1);
dt_prim_horario_w			ap_lote.dt_prim_horario%type;
dt_atend_lote_w				ap_lote.dt_atend_lote%type;
dt_limite_inicio_atend_w	ap_lote.dt_limite_inicio_atend%type;
dt_limite_atend_w			ap_lote.dt_limite_atend%type;
dt_limite_disp_farm_w		ap_lote.dt_limite_disp_farm%type;
dt_limite_entrega_setor_w	ap_lote.dt_limite_entrega_setor%type;
dt_limite_receb_setor_w		ap_lote.dt_limite_receb_setor%type;

c02 CURSOR FOR
	SELECT	b.nr_prescricao,
			a.nr_sequencia,
			a.cd_local_estoque,
			a.nr_seq_turno,
			a.nr_seq_classif,
			a.dt_inicio_turno,
			coalesce(a.nr_seq_local_geracao,'NO') nr_seq_local_geracao,
			a.nr_atendimento,
			b.cd_pessoa_fisica,
			a.dt_prim_horario,
			dt_atend_lote,
			dt_limite_inicio_atend,
			dt_limite_atend,
			dt_limite_disp_farm,
			dt_limite_entrega_setor,
			dt_limite_receb_setor
	from	ap_lote a,
			prescr_medica b,
			ap_lote_agrupamento c
	where 	a.nr_prescricao = b.nr_prescricao
	and 	b.nr_prescricao = c.nr_prescricao
	and 	a.ie_status_lote = 'G'
	and 	coalesce(a.ie_agrupamento,'N') = 'N'
	and 	coalesce(a.dt_inicio_dispensacao::text, '') = ''
	and 	coalesce(a.dt_impressao::text, '') = ''
	and 	coalesce(a.nr_lote_agrupamento::text, '') = ''
	and 	exists (SELECT	1
					from	parametros_farmacia x
					where 	x.cd_estabelecimento = b.cd_estabelecimento
					and 	coalesce(x.ie_agrupar_lote_copia,'N') = 'S')
	order by
			a.nr_atendimento,
			b.cd_pessoa_fisica,
			a.cd_local_estoque,
			a.nr_seq_turno,
			a.nr_seq_classif,
			coalesce(a.nr_seq_local_geracao,'NO'),
			a.dt_inicio_turno,
			a.nr_sequencia;


BEGIN
CALL gravar_log_lote_agrup(null,null,null,'Inicio','Tasy');
open C02;
loop
fetch C02 into	
	nr_prescricao_w,
	nr_seq_lote_w,
	cd_local_estoque_w,
	nr_seq_turno_w,
	nr_seq_classif_w,
	dt_inicio_turno_w,
	nr_seq_local_geracao_w,
	nr_atendimento_w,
	cd_pessoa_fisica_w,
	dt_prim_horario_w,
	dt_atend_lote_w,
	dt_limite_inicio_atend_w,
	dt_limite_atend_w,
	dt_limite_disp_farm_w,
	dt_limite_entrega_setor_w,
	dt_limite_receb_setor_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	
	/*Agrupa lotes apenas no mesmo dia*/

	select	coalesce(max(ie_desdobrar_agrup_dia_dif),'N')
	into STRICT	ie_gerar_lote_turno_dia_w
	from	parametros_farmacia
	where 	cd_estabelecimento = (	SELECT	max(cd_estabelecimento)
									from	prescr_medica
									where 	nr_prescricao = nr_prescricao_w);
	
	/*Verifica se existe mais de um lote a ser agruapado*/

	select 	count(1)
	into STRICT	qt_lotes_agrup_w
	from	ap_lote a
	where	a.ie_status_lote = 'G'
	and 	coalesce(a.ie_agrupamento,'N') 			= 'N'
	and 	a.cd_local_estoque 					= cd_local_estoque_w
	and 	a.nr_seq_turno 						= nr_seq_turno_w
	and 	a.nr_seq_classif 					= nr_seq_classif_w
	and 	coalesce(a.nr_seq_local_geracao,'NO')	= nr_seq_local_geracao_w
	and 	coalesce(a.dt_inicio_dispensacao::text, '') = ''
	and 	coalesce(a.dt_impressao::text, '') = ''
	and 	((PKG_DATE_UTILS.start_of(a.dt_inicio_turno,'dd',0) = PKG_DATE_UTILS.start_of(dt_inicio_turno_w,'dd',0))
	or (ie_gerar_lote_turno_dia_w = 'N'))
	and 	a.nr_prescricao in (	SELECT	b.nr_prescricao
									from	ap_lote_agrupamento b
									where 	((b.nr_atendimento = nr_atendimento_w) or (coalesce(b.nr_atendimento::text, '') = '' and b.cd_pessoa_fisica = cd_pessoa_fisica_w)));															
	
	/*Verifica se ja existe um agrupamento gerado para o truno/classif/local*/

	select 	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	ap_lote a,
			prescr_medica b
	where	a.nr_prescricao = b.nr_prescricao
	and 	((a.nr_atendimento = nr_atendimento_w) or (coalesce(a.nr_atendimento::text, '') = '' and b.cd_pessoa_fisica = cd_pessoa_fisica_w))
	and 	coalesce(a.dt_inicio_dispensacao::text, '') = ''
	and 	coalesce(a.dt_impressao::text, '') = ''
	and 	a.ie_status_lote = 'G'
	and 	coalesce(a.ie_agrupamento,'N') 			= 'S'
	and 	a.cd_local_estoque 					= cd_local_estoque_w
	and 	coalesce(a.nr_seq_local_geracao,'NO')	= nr_seq_local_geracao_w
	and 	a.nr_seq_turno 						= nr_seq_turno_w
	and 	a.nr_seq_classif 					= nr_seq_classif_w
	and 	((PKG_DATE_UTILS.start_of(a.dt_inicio_turno,'dd',0) = PKG_DATE_UTILS.start_of(dt_inicio_turno_w,'dd',0))
	or (ie_gerar_lote_turno_dia_w = 'N'));
	
	/*Grava log dos lotes agrupados*/

	qt_cont_w := qt_cont_w+1;
	CALL gravar_log_lote_agrup(nr_atendimento_w,nr_prescricao_w,nr_seq_lote_w,substr(' qt_cont_w=' || qt_cont_w ||
	' nr_seq_local_geracao_w=' || nr_seq_local_geracao_w ||	' dt_inicio_turno_w=' || dt_inicio_turno_w ||
	' cd_local_estoque_w=' || cd_local_estoque_w ||	' nr_seq_turno_w=' || nr_seq_turno_w ||' nr_seq_classif_w=' || nr_seq_classif_w ||
	' cd_estabelecimento_p=' || cd_estabelecimento_p || ' qt_lotes_agrup_w=' || qt_lotes_agrup_w || ' nr_seq_lote_w=' || nr_seq_lote_w ||
	' cd_local_estoque_ant_w=' || cd_local_estoque_ant_w ||' nr_seq_turno_ant_w=' || nr_seq_turno_ant_w || ' nr_seq_classif_ant_w=' || nr_seq_classif_ant_w ||
	' nr_seq_local_geracao_ant_w=' || nr_seq_local_geracao_ant_w ||' dt_inicio_turno_w=' || dt_inicio_turno_w || ' nr_prescricao_w=' || nr_prescricao_w || 
	' nr_atendimento_w=' || nr_atendimento_w || ' nr_atendimento_ant_w= ' || nr_atendimento_ant_w ||' dt_incio_turno_ant_w=' || dt_incio_turno_ant_w ||
	' cd_pessoa_fisica_ant_w=' || cd_pessoa_fisica_ant_w || ' cd_pessoa_fisica_w=' || cd_pessoa_fisica_w ||
	' nr_local_geracao_assoc_w=' || nr_local_geracao_assoc_w ||
	' nr_sequencia_w=' || nr_sequencia_w || ' ie_gerar_lote_turno_dia_w=' || ie_gerar_lote_turno_dia_w,1,2000),qt_cont_w);

	begin
	
	if	(qt_lotes_agrup_w > 1 AND nr_sequencia_w = 0) then
			
		select	nextval('ap_lote_seq')
		into STRICT	nr_sequencia_w
		;

		insert into ap_lote(
			nr_sequencia,
			dt_inicio_turno,
			dt_prim_horario,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_geracao_lote,
			nr_prescricao,
			nr_seq_turno,
			ie_status_lote,
			cd_setor_atendimento,
			nr_seq_classif,
			nm_usuario_geracao,
			ds_maquina_geracao,
			cd_perfil_geracao,
			qt_min_atraso_inicio_atend,
			qt_min_atraso_atend,
			qt_min_atraso_disp,
			qt_min_atraso_entrega,
			qt_min_atraso_receb,
			cd_tipo_baixa,
			ie_conta_paciente,
			ie_atualiza_estoque,
			cd_local_estoque,
			qt_prioridade,
			ie_origem_lote,
			ds_origem_ger_lote,
			ie_agrupamento,
			nr_seq_local_geracao,
			nr_atendimento,
			dt_atend_lote,
			dt_limite_inicio_atend,
			dt_limite_atend,
			dt_limite_disp_farm,
			dt_limite_entrega_setor,
			dt_limite_receb_setor,
			ie_geracao_rotina)
			(SELECT 
			nr_sequencia_w,
			dt_inicio_turno,
			dt_prim_horario,
			clock_timestamp(),
			coalesce(nm_usuario_p,'Tasy'),
			clock_timestamp(),
			coalesce(nm_usuario_p,'Tasy'),
			clock_timestamp(),
			nr_prescricao,
			nr_seq_turno,
			'G',
			cd_setor_atendimento,
			nr_seq_classif,
			coalesce(nm_usuario_p,'Tasy'),
			substr(obter_inf_sessao(0) || ' - ' || obter_inf_sessao(1),1,80),
			wheb_usuario_pck.get_cd_perfil,
			qt_min_atraso_inicio_atend,    
			qt_min_atraso_atend,
			qt_min_atraso_disp,
			qt_min_atraso_entrega,
			qt_min_atraso_receb,
			cd_tipo_baixa,
			ie_conta_paciente,
			ie_atualiza_estoque,
			cd_local_estoque,
			qt_prioridade,
			ie_origem_lote,
			'LA',
			'S',
			nr_seq_local_geracao,
			nr_atendimento_w,
			dt_atend_lote,
			dt_limite_inicio_atend,
			dt_limite_atend,
			dt_limite_disp_farm,
			dt_limite_entrega_setor,
			dt_limite_receb_setor,
			ie_geracao_rotina
			from ap_lote
			where nr_sequencia = nr_seq_lote_w);
			
		update	ap_lote
		set 	nr_lote_agrupamento = nr_sequencia_w,
				dt_impressao = clock_timestamp(),
				nm_usuario = 'BL1812'
		where	nr_sequencia = nr_seq_lote_w;
		
		update	ap_lote
		set		ds_prescricoes = substr((ds_prescricoes || ' ,' || nr_prescricao_w),1,255)
		where 	nr_sequencia = nr_sequencia_w
		and 	coalesce(position(nr_prescricao_w in ds_prescricoes),0) = 0;	
	
		update	ap_lote
		set		dt_prim_horario = dt_prim_horario_w,
				dt_atend_lote = dt_atend_lote_w,
				dt_limite_inicio_atend = dt_limite_inicio_atend_w,
				dt_limite_atend = dt_limite_atend_w,
				dt_limite_disp_farm = dt_limite_disp_farm_w,
				dt_limite_entrega_setor = dt_limite_entrega_setor_w,
				dt_limite_receb_setor = dt_limite_receb_setor_w
		where 	nr_sequencia = nr_sequencia_w
		and 	dt_prim_horario > dt_prim_horario_w;
	
	elsif (nr_sequencia_w > 0) then
	
		update	ap_lote
		set 	nr_lote_agrupamento = nr_sequencia_w,
				dt_impressao = clock_timestamp(),
				nm_usuario = 'BL1812'
		where	nr_sequencia = nr_seq_lote_w;
		
		update	ap_lote
		set		ds_prescricoes = substr((ds_prescricoes || ' ,' || nr_prescricao_w),1,255)
		where 	nr_sequencia = nr_sequencia_w
		and 	coalesce(position(nr_prescricao_w in ds_prescricoes),0) = 0;	
	
		update	ap_lote
		set		dt_prim_horario = dt_prim_horario_w,
				dt_atend_lote = dt_atend_lote_w,
				dt_limite_inicio_atend = dt_limite_inicio_atend_w,
				dt_limite_atend = dt_limite_atend_w,
				dt_limite_disp_farm = dt_limite_disp_farm_w,
				dt_limite_entrega_setor = dt_limite_entrega_setor_w,
				dt_limite_receb_setor = dt_limite_receb_setor_w
		where 	nr_sequencia = nr_sequencia_w
		and 	dt_prim_horario > dt_prim_horario_w;
	end if;
	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
	exception
	when others then
		rollback;
		CALL gravar_log_lote_agrup(nr_atendimento_w,nr_prescricao_w,nr_seq_lote_w,'Erro=' || SQLERRM(SQLSTATE) || DBMS_UTILITY.FORMAT_ERROR_BACKTRACE,coalesce(nm_usuario_p,'Tasy'));
	end;
	
	end;
end loop;
close C02;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_lote_agrupamento ( nr_atendimento_p bigint, cd_pessoal_fisica_p text, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

