SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_lote_audit_recurso (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_inicial_w		timestamp;
dt_final_w		timestamp;
cd_convenio_w		integer;
ie_acao_w		smallint;
cd_estabelecimento_w	integer;
nr_seq_ret_filtro_w	bigint;
qt_item_w		integer	:=0;
nm_usuario_resp_w	varchar(15);


BEGIN 
 
select	dt_inicial, 
	dt_final, 
	cd_convenio, 
	ie_acao_historico, 
	cd_estabelecimento, 
	nm_usuario_resp, 
	nr_seq_ret_filtro 
into STRICT	dt_inicial_w, 
	dt_final_w, 
	cd_convenio_w, 
	ie_acao_w, 
	cd_estabelecimento_w, 
	nm_usuario_resp_w, 
	nr_seq_ret_filtro_w 
from	lote_audit_recurso 
where	nr_sequencia = nr_sequencia_p;
 
update	conta_paciente_ret_hist 
set	nr_seq_lote_recurso = nr_sequencia_p 
where	nr_sequencia in ( 
	SELECT	b.nr_sequencia 
	from	conta_paciente d, 
		hist_audit_conta_paciente c, 
		conta_paciente_ret_hist b, 
		conta_paciente_retorno a 
	where	a.nr_sequencia		= b.nr_seq_conpaci_ret 
	and	d.nr_interno_conta	= a.nr_interno_conta 
	and	a.nr_interno_conta	= d.nr_interno_conta 
	and	b.nr_seq_hist_audit	= c.nr_sequencia 
	and	a.cd_convenio		= cd_convenio_w 
	and	d.cd_estabelecimento	= cd_estabelecimento_w 
	and	((b.nm_usuario_resp	= nm_usuario_resp_w) or (coalesce(nm_usuario_resp_w::text, '') = '')) 
	and	b.dt_historico between	dt_inicial_w and dt_final_w 
	and	coalesce(b.nr_seq_lote_recurso::text, '') = '' 
	and	c.ie_acao		= coalesce(ie_acao_w, c.ie_acao) 
	and	((exists (SELECT 1 
			from	convenio_retorno_item z 
			where	z.nr_sequencia		= b.nr_seq_ret_item 
			and	z.nr_seq_retorno		= nr_seq_ret_filtro_w)) or 
			coalesce(nr_seq_ret_filtro_w::text, '') = '')		-- afstringari 163042 
	and	((exists (select	1 			-- Edgar 16/04/2009, OS 137698, somente trazer contas com ttiulos com saldo 
			from	titulo_receber x 
			where	x.nr_interno_conta = d.nr_interno_conta 
			and	x.vl_saldo_titulo > 0 
			
union
 
			select	1 
			from	titulo_receber x 
			where 	x.nr_seq_protocolo = d.nr_seq_protocolo 
			and	coalesce(x.nr_interno_conta::text, '') = '' 
			and	x.vl_saldo_titulo > 0)) or (exists (select	1 
			from	convenio_retorno_item y 
			where	y.nr_interno_conta	= a.nr_interno_conta 
			and	y.cd_autorizacao	= a.cd_autorizacao 
			and	y.vl_amenor_post	> 0)) 
		) 
	);
 
select	count(*) 
into STRICT	qt_item_w 
from	conta_paciente_ret_hist 
where	nr_seq_lote_recurso = nr_sequencia_p;
 
if (qt_item_w > 0) then 
 
	update	lote_audit_recurso 
	set	dt_geracao = clock_timestamp(), 
		nm_usuario = nm_usuario_p 
	where	nr_sequencia = nr_sequencia_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
