CREATE OR REPLACE PROCEDURE gerar_lote_ext_consulta (nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_lote_p bigint, cd_cgc_p text, nm_usuario_p text) AS $body$
DECLARE


qt_exame_w			integer;
qt_lote_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_exame_w
from	exame_laboratorio b,
		prescr_procedimento a
where 	a.nr_seq_exame 	= b.nr_seq_exame
and		a.nr_prescricao = nr_prescricao_p
and		a.nr_sequencia	= nr_seq_prescr_p
and		b.cd_cgc_externo		= cd_cgc_p;

select 	count(*)
into STRICT	qt_lote_w
from	prescr_procedimento
where	nr_prescricao = nr_prescricao_p
and		nr_sequencia = nr_seq_prescr_p
and		(nr_seq_lote_externo IS NOT NULL AND nr_seq_lote_externo::text <> '');

if (qt_exame_w > 0) and (qt_lote_w = 0)	then

	update	prescr_procedimento
	set		nr_seq_lote_externo = nr_seq_lote_p
	where	nr_prescricao 	= nr_prescricao_p
	and		nr_sequencia	= nr_seq_prescr_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
