CREATE OR REPLACE PROCEDURE gerar_lote_producao_individual ( nr_lote_producao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_prevista_w	double precision;
i		bigint := 0;


BEGIN

select	max(qt_prevista)
into STRICT	qt_prevista_w
from	lote_producao
where	nr_lote_producao = nr_lote_producao_p;

while(i < trunc(qt_prevista_w)) loop
	insert into lp_individual(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_lote_producao,
		ie_status,
		dt_preparo,
		nm_usuario_preparo)
	values (
		nextval('lp_individual_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_lote_producao_p,
		'G',
		null,
		null);
	commit;
	i := i + 1;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
