CREATE OR REPLACE PROCEDURE gerar_lote_producao_opme_ws_js ( ds_lista_p text, nr_lote_producao_p INOUT bigint, cd_setor_atendimento_p bigint, nr_prescricao_p bigint, nr_ficha_tecnica_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_local_estoque_w	smallint;
nr_pos_virgula_w	bigint;
cd_material_w		integer;
nr_lote_producao_w	bigint;
ds_lista_w		varchar(2000);

BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
 
	select	cd_local_estoque 
	into STRICT	cd_local_estoque_w 
	from	setor_atendimento 
	where	cd_setor_atendimento = cd_setor_atendimento_p;
 
	nr_lote_producao_w := gerar_lote_producao_opme_ws( 
		nr_ficha_tecnica_p, cd_estabelecimento_p, nm_usuario_p, cd_local_estoque_w, nr_lote_producao_w, nr_prescricao_p);
 
	if (ds_lista_p IS NOT NULL AND ds_lista_p::text <> '') then 
		begin 
		ds_lista_w	:= ds_lista_p;
 
		while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop 
			begin 
			nr_pos_virgula_w	:= position(',' in ds_lista_w);
 
			if (nr_pos_virgula_w > 0) then 
				begin 
				cd_material_w	:= (substr(ds_lista_w,0,nr_pos_virgula_w -1))::numeric;
				ds_lista_w	:= substr(ds_lista_w,nr_pos_virgula_w +1, length(ds_lista_w));
				end;
			else 
				begin 
				cd_material_w	:= (ds_lista_w)::numeric;
				ds_lista_w	:= null;
				end;
			end if;
 
			if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') then 
				begin 
				CALL gerar_lote_prod_comp_opme_ws(nr_lote_producao_w,cd_material_w,nm_usuario_p);
				end;
			end if;
			end;
		end loop;
		end;
	end if;
	end;
end if;
commit;
nr_lote_producao_p	:= nr_lote_producao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
