CREATE OR REPLACE PROCEDURE gerar_lote_producao ( nr_ficha_tecnica_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, cd_local_estoque_p bigint, nr_lote_producao_p INOUT bigint) AS $body$
DECLARE

 
cd_material_w			integer;
cd_unidade_medida_consumo_w	varchar(30);
cd_setor_atendimento_w		integer;
cd_local_estoque_w		smallint;
nr_lote_producao_w		bigint;
				

BEGIN 
 
select	max(cd_material) 
into STRICT	cd_material_w 
from	ficha_tecnica 
where	nr_ficha_tecnica = nr_ficha_tecnica_p;
 
if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') then 
	begin 
	select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMS'),1,30) cd_unidade_medida_consumo 
	into STRICT	cd_unidade_medida_consumo_w 
	from	material 
	where	cd_material = cd_material_w;
 
	select	nextval('lote_producao_seq') 
	into STRICT	nr_lote_producao_w 
	;
	 
	insert into lote_producao( 
		nr_lote_producao, 
		cd_material, 
		ie_status_lote, 
		cd_unidade_medida, 
		qt_prevista, 
		cd_estabelecimento, 
		cd_local_estoque, 
		dt_atualizacao, 
		nm_usuario, 
		ie_etapa, 
		cd_farmaceutico, 
		ie_tipo_lote, 
		dt_inicio, 
		dt_geracao, 
		nm_usuario_geracao, 
		cd_responsavel) 
	values ( 
		nr_lote_producao_w, 
		cd_material_w, 
		'P', 
		cd_unidade_medida_consumo_w, 
		1, 
		cd_estabelecimento_p, 
		cd_local_estoque_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		'G', 
		null, 
		'A', 
		clock_timestamp(), 
		clock_timestamp(), 
		nm_usuario_p, 
		obter_dados_usuario_opcao(nm_usuario_p,'C'));
	end;
	 
	CALL gerar_lote_producao_comp_medic(nr_lote_producao_w, nm_usuario_p);
	 
	nr_lote_producao_p	:= nr_lote_producao_w;
 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
