CREATE OR REPLACE PROCEDURE gerar_man_ordem_serv_pr_ct ( nr_seq_ordem_serv_p man_ordem_serv_pr_ct.nr_seq_ordem_serv%type, nr_product_requirement_p man_ordem_serv_pr_ct.nr_product_requirement%type, nr_seq_caso_teste_p man_ordem_serv_pr_ct.nr_seq_caso_teste%type, ie_selecionado_p man_ordem_serv_pr_ct.ie_selecionado%type, nm_usuario_p man_ordem_serv_pr_ct.nm_usuario%type, ds_justificativa_p man_ordem_serv_pr_ct.ds_justificativa%type default null) AS $body$
DECLARE


nr_sequencia_w	man_ordem_serv_pr_ct.nr_sequencia%type;

BEGIN

if (nr_seq_caso_teste_p IS NOT NULL AND nr_seq_caso_teste_p::text <> '') then
	
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	man_ordem_serv_pr_ct
	where	nr_seq_caso_teste = nr_seq_caso_teste_p
	and	nr_seq_ordem_serv = nr_seq_ordem_serv_p
	and	nr_product_requirement = nr_product_requirement_p;
	
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	man_ordem_serv_pr_ct
		set	ie_selecionado = ie_selecionado_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp(),
			ds_justificativa = ds_justificativa_p
		where	nr_sequencia = nr_sequencia_w;
	else
		insert into man_ordem_serv_pr_ct(	nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nr_seq_ordem_serv,
			nr_product_requirement,
			nr_seq_caso_teste,
			ie_selecionado,
			ds_justificativa
		) values (
			nextval('man_ordem_serv_pr_ct_seq'),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_ordem_serv_p,
			nr_product_requirement_p,
			nr_seq_caso_teste_p,
			ie_selecionado_p,
			ds_justificativa_p
		);
	end if;
		
	commit;
end if;

end;
